import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import './assets/styles/main.css';


// const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 1000 });

class Demo extends React.Component {
  constructor() {
    super();
    this.state = {
      width: window.innerWidth,
    };
  }

  componentDidMount() {
    // 添加改变窗口事件,可加setTimeout
    EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
  }

  onFocus = (e) => {
    this.dom = e.target;
    this.barAnimate();
  }

  barAnimate = () => {
    if (!this.dom) {
      return;
    }
    const bar = this.bar;
    bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
  }
  componentDidMount() {
    this.handleWindowSizeChange();
    window.addEventListener('resize',  this.handleWindowSizeChange.bind(this));
}

  componentWillMount() {
    window.addEventListener('resize', this.handleWindowSizeChange);
  }

  // make sure to remove the listener
  // when the component is not mounted anymore
  componentWillUnmount() {
    window.removeEventListener('resize', this.handleWindowSizeChange);
  }

  handleWindowSizeChange = () => {
    console.log("I've been resized!");
  };

  render() {
    const { width } = this.state;
    const isMobile = width <= 800;

  if (isMobile) {
    return (
        <div className="container"></div>
      );
  } else {
    return (
      <div className="container">
      {/* <div className="nav">
        <div className="nav-wap">
          <Link
            className="nav-list"
            to="page0"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page0
          </Link>
          <Link
            className="nav-list"
            to="page1"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page1
          </Link>
          <Link
            className="nav-list"
            to="page2"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page2
          </Link>
          <Link
            className="nav-list"
            to="page3"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page3
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
      </div> */}
      <div className="nav">
        <div className="logo">
          <p>Ale Almeida</p>
        </div>
      </div>
      <Element className="pack-page page0" id="page0">
      <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[50, 1000]} delay={1000}
          duration={[1000, 3000]}
          ease={'easeInOutQuart'} leaveReverse>
        <div key='a'>Olá</div>
        <div key='b'>Sou Alexandre Almeida</div>
        <div key='c'>Senior UX Designer na Ogilvy & Mather Brasil</div>
        <div key='d'>Em São Paulo.</div>
      </QueueAnim>
      </Element>
        <ScrollOverPack
          id="page1"
          className="page1 fade-leave"
          ease="linear"
          location="page1"
          playScale={0.7}
          repeat={false}

        >
          <TweenOne className="tween-one" key="0"
          animation={{  delay: 1, duration: 0, translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Pop Credicard</h2>
            <div>Conteúdo 1</div>
          </TweenOne>
        </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page2"
        style={{ backgroundColor: '#ccc' }}
        id="page2"
        ease="linear"
        playScale={0.8}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Caixa</h2>
            <div>Conteúdo 2</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page3"
        id="page3"
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.8}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Vivo Easy</h2>
            <div>Conteúdo 3</div>
          </TweenOne>
      </ScrollOverPack>
    </div>

    );
    }
  }

}

ReactDOM.render(<Demo />, document.getElementById('root'));

