import React from 'react';
import Dialog from '@material-ui/core/Dialog';
import Tween from 'rc-tween-one';
import Fade from '@material-ui/core/Fade';
import Button from '@material-ui/core/Button';
import DialogTransition from './DialogTransition';
import content from './SectionsContent';
import CallDialog from './Dialog';
import ScrollOverPack from 'rc-scroll-anim/lib/ScrollOverPack';
import TweenOne from 'rc-tween-one';

const find = id => content.find(p => p.id === id);

function Transition(props) {
    return <Fade style={{
        width: '100vw'
    }} direction="down" {...props}/>;
}
class Dials extends React.Component {
    constructor({data, props}) {
        super(props);
        this.state = {
            open: false,
            reverse: true
        };
        this.i = find(data.params.i)
    }
    componentDidMount() {}
    handleClickOpen = () => {
        this.setState({open: true, reverse: false});
    };
    handleClose = () => {
        var dialog = document.getElementById('dialog');
        dialog.scrollTo(0, 0)
        this.setState({reverse: true});
        setTimeout(() => {
            this.setState({open: false});
        }, 2000);
    };
    render() {
        return (
                <TweenOne reverseDelay={5000} animation={[{  delay: 3000,opacity:0, duration: 1000},{delay: 0,opacity:1, duration: 1200}]}  style={{opacity: 0, position: 'fixed', height: '100vh',}}><Button
                    className="ver_mais"
                    onClick={this.handleClickOpen}
                    style={{
                    top: 0,
                    left: 0,
                    zIndex: 8,
                    cursor: 'pointer',
                    position: 'fixed',
                }}>
                    <i className="material-icons">trending_flat</i>
                </Button></TweenOne>


        )

    }
}
export default Dials;
