import content from './SectionsContent';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import iniciaScroll from './Scroll';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);
const CallDialog = ({m}) => {
    let a = find(m.p.id)
    return(
        <ScrollOverPack always={true} appear={true} playScale={0.5}>
        <Tween style={{position: 'absolute', zIndex: 9, color: a.cor3 }} animation={{ color: '#fff',opacity: 1, delay: 600, duration: 2600 }} component={'section'} 
                            className={"tween-one cabecalho"}>
                            <h1 style={{opacity: '0.86'}}>{a.letra}</h1>
                        </Tween>
        <Tween style={{position: 'relative', zIndex: 9, opacity: 0}} animation={{ opacity: 1, delay: 2000, duration: 1000 }} component={'section'} 
                            className={"tween-one cabecalho"}>
                            <div className='descricao'>{a.descricao}</div>
                            <div className='descricao'>{a.descricao}</div>
                            <div className='descricao'>{a.descricao}</div>
                            <div className='descricao'>{a.descricao}</div>
                            <label>{a.label}</label>
                            <h2>{a.h2}</h2>
                            <div className='descricao'>{a.descricao}</div>
                        </Tween>
                        </ScrollOverPack>
    )
}
