import ScrollAnim from 'rc-scroll-anim';



const iniciaScroll = () =>{
    ScrollAnim
    .scrollScreen
    .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});setTimeout(() => {
        window.scrollTo(0, 0);
    }, 50);
}


export default iniciaScroll;
