import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import { HashRouter as Router, Route} from 'react-router-dom';

import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';

import content from './SectionsContent';




const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;


function Sections() {
    return (
      <Conteudo  />
    )
  }
class VariableText extends React.Component {
    static propTypes = {
      isOpened: PropTypes.bool
    };


    static defaultProps = {
      isOpened: false
    };


    constructor(props) {
      super(props);
      this.state = {isOpened: this.props.isOpened, paragraphs: 0};
    }


    render() {
      const {isOpened, paragraphs} = this.state;

      return (
        <div>
          <div className="config">
            <label className="label">
              Opened:
              <input className="input"
                type="checkbox"
                checked={isOpened}
                onChange={({target: {checked}}) => this.setState({isOpened: checked})} />
            </label>

            <label className="label">
              Paragraphs:
              <input className="input"
                type="range"
                value={paragraphs} step={1} min={0} max={4}
                onChange={({target: {value}}) => this.setState({paragraphs: parseInt(value, 10)})} />
              {paragraphs}
            </label>
          </div>

          <Collapse isOpened={isOpened}>
            <div className="text">
              {paragraphs ? getText(paragraphs) : <p>No text</p>}
            </div>
          </Collapse>
        </div>
      );
    }
  }

  class Conteudo extends React.Component {


    render() {




      return (
        <span>
          <Element className="pack-page page0" id="page0"
            style={{ backgroundColor: '#ffffff' }}
          >
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>
            </QueueAnim>
          </Element>

          {content.map((route) => ( 

            <ScrollOverPack
              id={route.id} className={"pack-page  "+ route.id}
                style={{ backgroundColor: {...route.conteudo[0]}.bg , 'z-index': route.zindex }}
                playScale={0.1}
                >



                <TweenOne 
                      className="tween-one" key="0"
                      animation={{  delay: 200, duration: 0, translateY: 0, opacity: 1 }}
                      style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50 }}
                      >            {console.log({...route.idnum}    )}
                          <h2>{{...route.conteudo[0]}.titulo} </h2>
                          <div>{{...route.conteudo[0]}.descricao}</div>
                      </TweenOne>

            </ScrollOverPack>
          ))}
        </span>
      )
  }
}

export default Sections;
