import React from 'react';
import { HashRouter as Router, Route, Link } from "react-router-dom";
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
const EventListener = ScrollAnim.Event;

const find = (id) => content.find(p => p.id === id);

const Menu = ({i}) => {
    let a = find(i);
    return (
    <Router>
        <Route i={a} component={MontaMenu} />
    </Router>)
}

class MontaMenu extends React.Component {
    constructor({i}){
        super({i});
        // this.blur = MENU({params: this.onBlur})
        this.i = i;
    }

    onFocus = () =>{
        console.log(i.id + 'foco')
    }
    onBlur = () =>{
        console.log(this.i + 'deixou')
    }

render(){
    return (
        <nav className='nav'>
        {
            content.map(i => (
                <Link
                    className="nav-list"
                    exact
                    key={i.id}
                    to={i.hash}
                    toShowHeight
                    onFocus={this.onFocus}
                    onBlur={this.onBlur}
                    >
                    {'Example'+ i.id}
                </Link>
            ))
        }
        </nav>
    )

}
}

