import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import Animate from 'rc-animate';
import velocity from 'velocity-animate';
import './assets/styles/main.css';

const _package = require('../package.json');

// const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: false, duration: 1000 });

class Demo extends React.Component {
  componentDidMount() {
    // 添加改变窗口事件,可加setTimeout
    EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
  }

  onFocus = (e) => {
    this.dom = e.target;
    this.barAnimate();
  }

  barAnimate = () => {
    if (!this.dom) {
      return;
    }
    const bar = this.bar;
    bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
  }

  state = {
    destroyed: false,
    visible: true,
    exclusive: false,
  }

  toggleAnimate = () => {
    this.setState({
      visible: !this.state.visible,
    });
  }

  animateEnter = (node, done) => {
    let ok = false;

    function complete() {
      if (!ok) {
        ok = 1;
        done();
      }
    }

    node.style.display = 'none';

    velocity(node, 'slideDown', {
      duration: 2000,
      complete,
    });
    return {
      stop() {
        velocity(node, 'finish');
        // velocity complete is async
        complete();
      },
    };
  }
  animateLeave = (node, done) => {
    let ok = false;

    function complete() {
      if (!ok) {
        ok = 1;
        done();
      }
    }

    node.style.display = 'block';

    velocity(node, 'slideUp', {
      duration: 1000,
      complete,
    });
    return {
      stop() {
        velocity(node, 'finish');
        // velocity complete is async
        complete();
      },
    };
  }
  toggle = (field) => {
    this.setState({
      [field]: !this.state[field],
    });
  }

  destroy = () => {
    this.setState({
      destroyed: true,
    });
  }


  render() {
    const anim = {
      enter: this.animateEnter,
      leave: this.animateLeave,
    };
    return (<div>
      {/* <div className="nav">
        <div className="nav-wap">
          <Link
            className="nav-list"
            to="page0"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page0
          </Link>
          <Link
            className="nav-list"
            to="page1"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page1
          </Link>
          <Link
            className="nav-list"
            to="page2"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page2
          </Link>
          <Link
            className="nav-list"
            to="page3"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page3
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
      </div> */}
      <div className="nav">
        <div className="logo">
          <p>Ant Motion</p>
        </div>
      </div>
      <Element className="pack-page page0" id="page0">
          <div className="page-title" key="title">
            <p>Intro</p>
          </div>
          <div className="page-description" key="c">
            <p>A scrollScreen demo</p>
          </div>
      </Element>
        <ScrollOverPack
          id="page1"
          className="page1 fade-leave"
          ease="linear"
          location="page1"
          playScale={0.8}
          repeat={false}
        >
          <TweenOne className="tween-one" key="0"
          animation={{  delay: 5, duration: 0, translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Título</h2>
            <div>Conteúdo 1</div>
          </TweenOne>
        </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page2"
        style={{ backgroundColor: '#ccc' }}
        id="page2"
        ease="linear"
        playScale={0.8}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 5, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Título 2</h2>
            <div>Conteúdo 2</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page3"
        id="page3"
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.8}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 5, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Título 3</h2>
            <div>Conteúdo 3</div>
          </TweenOne>
      </ScrollOverPack>
    </div>);
  }
}

ReactDOM.render(<Demo />, document.getElementById('root'));

