import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import { HashRouter as Router, Route} from 'react-router-dom';

import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';

import content from './SectionsContent';


import 'rc-collapse/assets/index.css';
// import 'string.prototype.repeat';
import Collapse, { Panel } from 'rc-collapse';


require('rc-collapse/assets/index.css');


const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
const ScrollParallax = ScrollAnim.Parallax;
const ScrollElement = ScrollAnim.Element;


function Sections() {
    return (

      <Conteudo />

    )
  }





function random() {
  return parseInt(Math.random() * 10, 10) + 1;
}



const arrowPath = 'M869 487.8L491.2 159.9c-2.9-2.5-6.6-3.9-10.5-3.9h-88' +
  '.5c-7.4 0-10.8 9.2-5.2 14l350.2 304H152c-4.4 0-8 3.6-8 8v60c0 4.4 3.' +
  '6 8 8 8h585.1L386.9 854c-5.6 4.9-2.2 14 5.2 14h91.5c1.9 0 3.8-0.7 5.' +
  '2-2L869 536.2c14.7-12.8 14.7-35.6 0-48.4z';

function expandIcon({ isActive }) {
  return (
    <i style={{ marginRight: '.5rem' }}>
      <svg
        viewBox="0 0 1024 1024"
        width="1em"
        height="1em"
        fill="currentColor"
        style={{
          verticalAlign: '-.125em',
          transition: 'transform .2s',
          transform: `rotate(${isActive ? 90 : 0}deg)`,
        }}
      >
        <path d={arrowPath} p-id="5827"></path>
      </svg>
    </i>

  );

}

  class Conteudo extends React.Component {
    constructor() {
      super();
      this.state = {
        show: false,
        css: { backgroundColor: '#174270', height: '1000vh' },
        cssNoPosition: true,
      };
    }
    state = {
      time: random(),
      accordion: false,
      activeKey: [''],
    }
    onChange = (activeKey) => {
      this.setState({
        activeKey,
      });
    }

    setCss = (e) => {
      const css = this.state.css;
      console.log(e);
      if (this.state.cssNoPosition) {
        css.position = 'fixed';
        css.top = 0;
        css.overflow = 'scroll';
      } else {
        css.position = '';
        css.top = '';
      }
      this.setState({
        css,
        cssNoPosition: !this.state.cssNoPosition,
      });
    };
    getItems(props, text) {
        const items = []; 

          const key = this.props;
          items.push(
            <Panel header={`This is panel header ${key}`} key={key} >
              <ScrollElement style={{ height: "1000vh"  }} id="Scroll-Pack">
        <ScrollParallax
          className="pack-page"
          location="Scroll-Pack"
          // animation={{
          //   backgroundColor: '#0097D0',
          //   playScale: [1],
          //   onStart: () => { this.setCss('start'); },
          //   // onCompleteBack: () => { this.setCss('back complete'); },
          //   // onComplete: () => { this.onComplete('complete'); },
          //   // onStartBack: () => { this.onComplete('back start'); },
          // }}
          style={this.state.css}
        >
        </ScrollParallax>
        </ScrollElement>
            </Panel>
          );


      return items;
    }

    setActivityKey = (props) => {
      this.setState({
        activeKey: [props],
      });
    }

    toggle = (props) => {
      this.key = props;
      this.setState({
        accordion: !this.state.accordion,
      });
    }

    reRender = () => {
      this.setState({
        time: random(),
      });
    }


    render() {

      const accordion = this.state.accordion;
      const activeKey = this.state.activeKey;
      return (

        <span>


          <Element className="pack-page page0" id="page0"
            style={{ backgroundColor: '#ffffff' }}
          >
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>
            </QueueAnim>
          </Element>

          {content.map((route) => ( 

            <ScrollOverPack
              id={route.id} className={"pack-page  "+ route.id}
                style={{ backgroundColor: {...route.conteudo[0]}.bg , 'z-index': route.zindex }}
                playScale={0.1}
                >



                <TweenOne 
                      className="tween-one" key="0"
                      animation={{  delay: 200, duration: 0, translateY: 0, opacity: 1 }}
                      style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50 }}
                      >            {console.log({...route.idnum}    )}
                          <h2>{{...route.conteudo[0]}.titulo} </h2>
                          <div>{{...route.conteudo[0]}.descricao}</div>


                          <Collapse className={route.id+'-collapse'}
                            accordion={accordion}
                            onChange={this.onChange}
                            activeKey={activeKey}
                            expandIcon={expandIcon}

                          >
                            {this.getItems()}

                          </Collapse>
                      </TweenOne>

            </ScrollOverPack>
          ))}

        </span>
      )
  }
}




export default Sections;
