import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import TweenOne from 'rc-tween-one';
import Tween from 'rc-tween-one';
import content from './SectionsContent';
import iniciaScroll from './Scroll';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);

const Twn =({d}) => {
    var i = d.p.n
    return (
<Tween

                    animation={[
                    {
                        delay: 0,
                        duration: 1000,
                        opacity: 0,
                        x: 0,
                        top: 0,
                        ease: "linear",
                        backgroundColor: i.cor2
                    }, {
                        delay: 1000,
                        duration: 300,
                        opacity: 1,
                        x: 0,
                        y: '0',
                        top: 0,
                        ease: "easeOutCirc",
                        backgroundColor: '#fff'
                    }
                ]}
                    style={{
                    opacity: 0,
                    transform: 'translateY(0)',
                    backgroundColor: '#fff',
                    top: '0vh'
                }}
                    className={"tween-one cabecalho"}>
                    <h1 style={{
                        color: d.p.cor2
                    }}>{d.p.letra}</h1>
                    <label>{d.p.label}</label>
                    <h2>{d.p.h2}</h2>
                    <div className='descricao'>{d.p.descricao}</div>
                </Tween>
    );
}
const OverPack =({data}) => {
    let n = find(data.params.id);
    return (
            <ScrollOverPack
                always={true}
                className={n.classe + n.id}
                id={n.hash + n.id}
                playScale={0.5}>
                    <Twn  key='tweenOne2'
                    reverseDelay={4000}
                    component={'section'} d={{
            p: n
        }} />
                <TweenOne
                    key="tweenOne"
                    animation={[
                    {
                        delay: 0,
                        duration: 1000,
                        opacity: 1,
                        top: '100vh',
                        height: '0vh',
                        ease: "easeOutCubic",
                        backgroundColor: n.cor1,
                        y: '0',
                        type: 'from'
                    }, {
                        delay: 2900,
                        duration: 2500,
                        opacity: 1,
                        top: '-100vh',
                        ease: "easeInOutExpo",
                        height: '100vh',
                        backgroundColor: n.cor2,
                        y: '0',
                        type: 'to'
                    }
                ]}
                    style={{
                    width: '100%',
                    height: '600vh',
                    position: 'fixed',
                    transform: 'translateY(0vh)',
                    backgroundColor: n.cor1,
                    opacity: 1,
                    top: '0vh',
                    zIndex: 9
                }}/>
            </ScrollOverPack>)
}
class Home extends React.Component {
    componentDidMount(){
        iniciaScroll();
    }
    render() {
        return (content.map((i) => (<OverPack
            data={{
            params: {
                id: i.id
            }
        }}/>)));
    }
}

export default Home;
