import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import { HashRouter as Router, Route} from 'react-router-dom';

const Link = ScrollAnim.Link;


function Sandwiches() {
  return <h2>Sandwiches</h2>;
}

function Tacos({ routes }) {
  return (
    <div>
      <h2>Tacos</h2>
      <ul>
        <li>
          <Link to="/tacos/bus">Bus</Link>
        </li>
        <li>
          <Link to="/tacos/cart">Cart</Link>
        </li>
      </ul>

      {routes.map((route, i) => (
        <LINK key={i} {...route} />
      ))}
    </div>
  );
}

function Bus() {
  return <h3>Bus</h3>;
}

function Cart() {
  return <h3>Cart</h3>;
}


const routes = [
  {
    path: "/sandwiches",
    component: Sandwiches
  },
  {
    path: "/tacos",
    component: Tacos,
    routes: [
      {
        path: "/tacos/bus",
        component: Bus
      },
      {
        path: "/tacos/cart",
        component: Cart
      }
    ]
  }
];

// wrap <Route> and use this everywhere instead, then when
// sub routes are added to any route it'll work
function menuItem(route) {
  return (
    <Link className={route.path} to={route.path} index={4} showHeightActive={['0%', '50%']}>
           {route.path}
          </Link>
  );
}

function Menu() {
  return (
    <Router>
        {routes.map((route, i) => (
          <RouteWithSubRoutes key={i} {...route} />
        ))}
    </Router>
  );
}

export default Menu;
