import React from 'react';
import {HashRouter as Router, Route, Link} from "react-router-dom";
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import Tween from 'rc-tween-one';
import content from './SectionsContent';
import CloseIcon from '@material-ui/icons/Close';
import DialogTransition from './DialogTransition';
import Slide from '@material-ui/core/Slide';
import iniciaScroll from './Scroll';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);

function DialTransition(props) {
    return <Slide direction="down" {...props}/>;
}

class CallDialog extends React.Component {
    constructor({d, props}) {
        super({d, props});
        this.state = {
            open: false,
            reverse: true
        };

        this.a = find(d.p.a);
    }
    handleClickOpen = () => {
        this.setState({reverse: false});
        setTimeout(() => {
            this.setState({open: true});
            setTimeout(() => {
                ScrollAnim
                    .scrollScreen
                    .unMount();
            }, 1000);
        }, 1000);
    };

    handleClose = () => {
        this.setState({reverse: true});
        setTimeout(() => {
            this.setState({open: false});
        }, 1000);
    };
    render() {
        // let a = this.a;
        // const dialStyles = {
        //     opacity: 1,
        //     transform: 'translateY(0)',
        //     backgroundColor: '#fff',
        //     top: '0vh'
        // }
        // const dialAnimation = {
        //     from: {
        //         delay: 1000,
        //         duration: 600,
        //         opacity: 1,
        //         x: 0,
        //         top: 0,
        //         ease: "linear",
        //         backgroundColor: a.cor2
        //     },
        //     to: {
        //         delay: 1000,
        //         duration: 300,
        //         opacity: 1,
        //         x: 0,
        //         y: '0',
        //         top: 0,
        //         ease: "easeOutCirc",
        //         backgroundColor: '#fff'
        //     }

        // };
        return (
            <span>
                {/* <Router> */}
                    <Button
                        className="ver_mais"
                        to={'anchor' + a.id}
                        onClick={this.handleClickOpen}
                        style={{
                        height: '4em',
                        left: 0,
                        top: 0,
                        zIndex: 999999,
                        cursor: 'pointer',
                        position: 'absolute'
                    }}>
                        {'ver projeto' + a.id}
                    </Button>
                {/* </Router> */}
                <Dialog fullScreen // className="dialog"
                    elevation={8} open={this.state.open} transitionDuration={{
                    enter: 0,
                    exit: 0
                }} style={{
                    backgroundColor: '#ffffff',
                    zIndex: 99999,
                    height: '100%',
                    position: 'absolute'
                }}>
                    {/* <ScrollOverPack always={true} appear={true} playScale={0.5}> */}
                        <Tween
                            component={'section'}
                            // dialAnimation={[
                            // {
                            //     ...dialAnimation.from
                            // }, {
                            //     ...dialAnimation.to
                            // }
                        // ]}
                            // style={dialStyles}
                            className={"tween-one cabecalho"}>
                            <h1
                                style={{
                                color: '#fff'
                            }}>{this.a.letra}</h1>
                            <label>{this.a.label}</label>
                            <h2>{this.a.h2}</h2>
                            <div className='descricao'>{this.a.descricao}</div>
                            <label>{this.a.label}</label>
                            <h2>{this.a.h2}</h2>
                            <div className='descricao'>{this.a.descricao}</div>

                        </Tween>
                        {/* {DialogTransition({a: this.a, key: 'tweenOne3', reverse: this.state.reverse, reverseDelay: 0})} */}
                    {/* </ScrollOverPack> */}
                    {/* <Router> */}
                        <Button
                            // to={'/'}
                            style={{
                            backgroundColor: 'transparent',
                            top: 0,
                            position: "fixed",
                            zIndex: 19,
                            right: '1em'
                        }}
                            onClick={this.handleClose}>
                            <CloseIcon
                                style={{
                                width: 'calc((100vw+100vh)/2/12)',
                                height: 'calc((100vw+100vh)/2/12)'
                            }}/>
                        </Button>
                    {/* </Router> */}
                </Dialog>

            </span>
        )
    }
}
// CallDialog.propTypes = {
//     classes: PropTypes.object.isRequired
// };
export default CallDialog;
