        opacity: 0,
        transform: 'translateY(0)',
        backgroundColor: '#fff',
        top: '0vh'
    }
    const animation = { 
        from:
            {
                delay: 1000,
                duration: 600,
                opacity: 0,
                x: 0,
                top: 0,
                ease: "linear",
                backgroundColor: n.cor2
            },
        to: 
            {
                delay: 1000,
                duration: 300,
                opacity: 1,
                x: 0,
                y: '0',
                top: 0,
                ease: "easeOutCirc",
                backgroundColor: '#fff'
            }

    };
    return (
        <Dialog
                            fullScreen
                            id={'anchor' + n.id}
                            className="pack-page"
                            elevation={8}
                            transitionDuration={{
                            enter: 0,
                            exit: 0
                        }}
                            TransitionComponent={Transition}
