                                    reverseDelay={800}
                                    reverse={this.state.buttonCloseReverse}
                                    animation={[{
                                        delay: 2000,
                                        duration: 600,
                                        rotate: -90,
                                        opacity: 1,
                                        x: 0,
                                        y: 0,
                                        ease: "easeInOutQuart"
                                    }
                                ]}
                                    style={{
                                    transform: 'rotate(0deg) translateX(-0.5em)translateY(-0.1em)',
                                    transformOrigin: '50%, 50%',
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)',
                                    position: 'fixed',
                                    opacity: 0,
                                    zIndex: '9999',
                                    textAlign: "center",
                                    top: '4em',
                                    right: '3em'
                                }}>
                                    <CloseIcon
                                        style={{
                                        width: 'calc((100vw+100vh)/2/12)',
                                        height: 'calc((100vw+100vh)/2/12)'
                                    }}/>
                                </Tween>
                            </Button>

                        </Dialog>
    )
}

export default Dial;
