import ScrollAnim from 'rc-scroll-anim';
// import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    }
};

function Transition(props) {
    return (<Fade in={true} { ...props }/>)
}

function Intro() {
    return (
        <span >
            <IntroContent/>
        </span>
    )
}
class IntroContent extends React.Component {
    handleClose = () => {
        this.setState({open: false});
        ScrollAnim
            .scrollScreen
            .unMount();
        ScrollAnim
            .scrollScreen
            .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    };
    render() {
        return (
            <div>
                <Link
                    className="nav-list logo"
                    to="page0"
                    showHeightActive={['50%', '10%']}
                    onClick={this.handleClose}>
                    Alê Almeida
                </Link>
                <Element
                    className="pack-page page0"
                    id='page_intro'
                    style={{
                    backgroundColor: '#ffffff',
                    zIndex: 1500
                }}>
                    <QueueAnim
                        key='queueAnim'
                        type={['right', 'alpha']}
                        className="intro"
                        interval={[200, 700]}
                        delay={800}
                        duration={[800, 500]}
                        playScale={0.1}>
                        <div key='a'>Olá!</div>
                        <div key='b'>Sou Alexandre Almeida,</div>
                        <div key='c'>Senior Product Designer na
                            <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                        </div>
                        <div key='d'></div>
                        <div key='e'>São Paulo.</div>

                    </QueueAnim>
                </Element>
            </div>
        );
    }
}
export default Intro;
