//     setTimeout(() => {
//         this.handleClickOpen()
//         this.setState({open: true});
//     }, 3400);
// }
class Play {
    // pseudo-código
  state = {open: false};
// pseudo-código
  setState(state, callback) {
    this.state = Object.assign({}, this.state, state);
    if (callback) callback();
  }
// múltiplas chamadas a funcional setState
 onPlay () {
    this.setState( (state) => ({open: true}) )
  }
}
const On= new Play();
function Twn({n}) {
    const styles = {
        opacity: 0,
        transform: 'translateY(0)',
        backgroundColor: '#fff',
        top: '0vh'
    }
    const animation = { 
        from:
            {
                delay: 1000,
                duration: 600,
                opacity: 0,
                x: 0,
                top: 0,
                ease: "linear",
                backgroundColor: n.cor2
            },
        to: 
            {
                delay: 1000,
                duration: 300,
                opacity: 1,
                x: 0,
                y: '0',
                top: 0,
                ease: "easeOutCirc",
                backgroundColor: '#fff'
            }

    };
    return (
        <Tween
        key='tweenOne2'
            reverseDelay={4000}
            component={'section'}
            animation={[ {
            ...animation.from},
            { ...animation.to}
        ]}
            style={styles}
            className={"tween-one cabecalho"}>
            <h1 style={{
                color: n.cor2
            }}>{n.letra}</h1>
            <label>{n.label}</label>
            <h2>{n.h2}</h2>
            <div className='descricao'>{n.descricao}</div>
            <Button
        onClick={onPlay}
        >
        <i class="material-icons ver_mais">
trending_flat
</i>
    </Button>
        </Tween>
    )
}

export default Twn;
