import ScrollAnim from 'rc-scroll-anim';
// import ReactDOM from 'react-dom';
import React from 'react';
import PropTypes from 'prop-types';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
// import Icon from '@material-ui/core/Icon'
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';

import Zoom from '@material-ui/core/Zoom';
import {
    withStyles
} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
// import {
//     Contexto,
//     datas
// } from './SectionsContexto';
// import Botao from './SectionsBotoes';
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

// function find(id) {
//     return content.find(p => p.id === id);
// }
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    },
};

function Transition(props) {
    return <Slide direction = "up" {...props} />
}

function Sections() {
    return <span> <Home /> </span>
        }

class Home extends React.Component {
            state = {
				open: false,
			  };
            handleClickOpen = () => {
                this.setState({
					open: true
				});
				ScrollAnim.scrollScreen.unMount();
            };
            handleClose = () => {
                this.setState({
                    open: false
				});
				ScrollAnim.scrollScreen.init({
					loop: false,
					duration: 1200,
					ease: "easeInOutQuart",
					scrollInterval: 800
				});
            };
            render() {
                const classes = this.props
                return ( 
					<div> <Intro match = {
                        {
                            params: {
                                id: 0
                            },
                            titulo: 'Intro'
                        }
                    }
                    /> {
                    content.map(i => ( < ScrollOverPack id = {
                                i.hash + i.id
                            }
                            className = {
                                i.classe + i.id
                            }
                            style = {
                                {
                                    backgroundColor: i.bg,
                                    'z-index': i.zindex
                                }
                            }
                            playScale = {
                                0.1
                            } >
							<TweenOne className = {
                                "tween-one conteudo-colapsado"
                            }
                            key = "0"
                            animation = {
                                {
                                    delay: 200,
                                    duration: 0,
                                    translateY: 0,
                                    opacity: 1
                                }
                            }
                            style = {
                                {
                                    transform: 'translateY(0px)',
                                    opacity: 0
                                }
                            } > 
							<h2> {
                                i.h2
                            } </h2> 
							<div> {
                                i.descricao
                            } </div> 
								<Button style={{position: 'fixed', zIndex: '9999', top: '1em', right: '1em' }} onClick={this.handleClickOpen}>Open full-screen dialog</Button>

						</TweenOne></ScrollOverPack>))} 
						<Dialog
								fullScreen
								scroll={this.state.scroll}
								open={this.state.open}
								onClose={this.handleClose}
								TransitionComponent={Transition}
								>

								<AppBar className={classes.appBar}>
									<Toolbar>
									<IconButton color="inherit" onClick={this.handleClose} aria-label="Close">
										<CloseIcon />
									</IconButton>
									<Typography variant="h6" color="inherit" className={classes.flex}>
										Sound
									</Typography>
									<Button color="inherit" onClick={this.handleClose}>
										save
									</Button>
									</Toolbar>
								</AppBar>
								<List>
									<ListItem button>
									<ListItemText primary="Phone ringtone" secondary="Titania" />
									</ListItem>
									<Divider />
									<ListItem button>
									<ListItemText primary="Default notification ringtone" secondary="Tethys" />
									</ListItem>
								</List>
								</Dialog>
					</div>);
                }
            }

            function Intro({ match}) {
                // let data = find(match.params.id);
                return ( < Element className = "pack-page page0"
                    id = 'page0'
                    style = {
                        {
                            backgroundColor: '#ffffff'
                        }
                    } > < QueueAnim key = 'queueAnim'
                    type = {
                        ['right', 'alpha']
                    }
                    className = "intro"
                    interval = {
                        [200, 700]
                    }
                    delay = {
                        800
                    }
                    duration = {
                        [800, 500]
                    }
                    playScale = {
                        0.1
                    } > < div key = 'a' > Olá! < /div> <
                    div key = 'b' > Sou Alexandre Almeida, < /div> <
                    div key = 'c' > Senior Product Designer na < a href = "http://ogilvy.com.br" > Ogilvy & Mather Brasil < /a>,</div > < div key = 'd' > < /div> <
                    div key = 'e' > São Paulo. < /div> < /QueueAnim > < /
                    Element > );
            }
            Home.propTypes = {
                classes: PropTypes.object.isRequired
            };
            export default withStyles(styles)(Sections);
