import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import Animate from 'rc-animate';
import './assets/styles/main.css';

const _package = require('../package.json');

// const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: false });

class Demo extends React.Component {
  componentDidMount() {
    // 添加改变窗口事件,可加setTimeout
    EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
  }

  onFocus = (e) => {
    this.dom = e.target;
    this.barAnimate();
  }

  barAnimate = () => {
    if (!this.dom) {
      return;
    }
    const bar = this.bar;
    bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
  }

  render() {
    return (<div>
      {/* <div className="nav">
        <div className="nav-wap">
          <Link
            className="nav-list"
            to="page0"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page0
          </Link>
          <Link
            className="nav-list"
            to="page1"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page1
          </Link>
          <Link
            className="nav-list"
            to="page2"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page2
          </Link>
          <Link
            className="nav-list"
            to="page3"
            showHeightActive="300"
            onFocus={this.onFocus}
          >
            Page3
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
      </div> */}
      <Element className="pack-page page0" id="page0">
        <QueueAnim className="home-title">
          <div className="page-title" key="title" style={{ height: 100, overflow: 'auto' }}>
            <p>Intro</p>
          </div>
          <div className="page-description" key="c">
            <p>A scrollScreen demo</p>
          </div>
        </QueueAnim>
      </Element>
      <div id="page1">
        <ScrollOverPack
          id="page1"
          style={{ backgroundColor: '#eee' }}
          className="page1"
          ease="linear"
          playScale={0}
          location="page1"
          repeat={1}
        >
          <TweenOne className="tween-one" key="0" 
          animation={{  delay: 190, duration: 140,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(-100px)', opacity: 0, position: 'relative' }}
          >
            <h2>Título</h2>
            <div>Conteúdo 1</div>
          </TweenOne>
        </ScrollOverPack>
        </div>

      <div id="page2">
      <ScrollOverPack
        className="pack-page page2"
        style={{ backgroundColor: '#ccc' }}
        id="page2"
        ease="linear"
        playScale={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 190, duration: 140,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(-100px)', opacity: 0, position: 'relative' }}
          >
            <h2>Título 2</h2>
            <div>Conteúdo 2</div>
          </TweenOne>
      </ScrollOverPack>
      </div>

      <div id="page3">
      <ScrollOverPack
        className="pack-page page3"
        id="page3"
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 190, duration: 140,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(-100px)', opacity: 0, position: 'relative' }}
          >
            <h2>Título 3</h2>
            <div>Conteúdo 3</div>
          </TweenOne>
      </ScrollOverPack>
      </div>
    </div>);
  }
}

ReactDOM.render(<Demo />, document.getElementById('root'));

