import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
import Twn from './Twn';
import Transition from './Transition';
import Dials from './Dials';
const ScrollOverPack = ScrollAnim.OverPack;


const find = id => content.find(p => p.id === id);
const OverPack = ({data}) => {
    let n = find(data.params.id);

    return (
        <ScrollOverPack
            always={true}
            // appear={true}
            className={n.classe}
            id={n.hash}
            data={'dial'+n.id}
            playScale={1}>
            {Twn({n: n, key: 'tweenOne_2'})}
            {Transition({n: n, key: 'tweenOne_1'+n.id})}

            <Dials data={{params: {i: n.id}}}/>
        </ScrollOverPack>
    )
}
class Home extends React.Component {
    componentDidMount() {
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 0, ease: "easeInOutCirc", scrollInterval: 3000});
        });
    }
    render() {

        return (content.map((i) => (
                <OverPack key={'op'+i.id}
                    data={{
                    params: {
                        id: i.id
                    }
                }}/>
        )));
    }
}

export default Home;
