import React from 'react';
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import Tween from 'rc-tween-one';
import content from './SectionsContent';
import CloseIcon from '@material-ui/icons/Close';
import DialogTransition from './DialogTransition';
import iniciaScroll from './Scroll';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);



class CallDialog extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            open: false,
            reverse: true
        };

        this.a = find(data.params.id);
    }
      handleClickOpen = () => {

        this.setState({ reverse: false });

        this.setState({ open: true });
      };



      handleClose = () => {
        this.setState({ reverse: true});
        setTimeout(() => {
            this.setState({ open: false });
         }, 3000);
      };
    render() {
        // const a = this.a;
       const dialStyles = {
            opacity: 1,
            transform: 'translateY(0)',
            backgroundColor: '#fff',
            top: '0vh'
        }
        const dialAnimation = {
            from: {
                delay: 1000,
                duration: 600,
                opacity: 1,
                x: 0,
                top: 0,
                ease: "linear",
                backgroundColor: this.a.cor2
            },
            to: {
                delay: 1000,
                duration: 300,
                opacity: 1,
                x: 0,
                y: '0',
                top: 0,
                ease: "easeOutCirc",
                backgroundColor: '#fff'
            }

        };
        return (
            <div>
            <Button
                className="ver_mais"
                onClick={this.handleClickOpen}
                style={{
                height: '4em',
                left: 0,
                zIndex: 999999,
                cursor: 'pointer',
            }}>
                 {'ver projeto' + this.a.id}
            </Button>
<Dialog 
                fullScreen
                className="dialog"
                // transitionComponent={DialTransition}
                elevation={8}
                open={this.state.open}
                transitionDuration={{
                enter: 0,
                exit: 0
            }}
                style={{
                backgroundColor: '#ffffff',
                zIndex: 99999,
                height: '100%',
                position: 'absolute'
            }}
            >
        <ScrollOverPack
            always={true}
            appear={true}
            playScale={0.5}
            >
            <Tween
                component={'section'}
                dialAnimation={[
                {
                    ...dialAnimation.from
                }, {
                    ...dialAnimation.to
                }
            ]}
            style={dialStyles}
            className={"tween-one cabecalho"}>
                <h1 style={{
                    color: '#fff'
                }}>{a.letra}</h1>
                <label>{a.label}</label>
                <h2>{a.h2}</h2>
                <div className='descricao'>{a.descricao}</div>
                <label>{a.label}</label>
                <h2>{a.h2}</h2>
                <div className='descricao'>{a.descricao}</div>

                <Button
                            style={{
                            backgroundColor: 'transparent',
                            top:0,
                            position: "fixed",
                            zIndex: 19,
                            right: '1em'
                        }}
                            onClick={this.handleClose}
                            >
                                <CloseIcon
                                    style={{
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)'
                                }}/>
                        </Button>
               </Tween>
               {DialogTransition({a: a, key: 'tweenOne3', reverse: this.state.reverse, 
                reverseDelay: 0} )}

        </ScrollOverPack>

            </Dialog>
            </div>
        )
    }
}
CallDialog.propTypes = {
    classes: PropTypes.object.isRequired,
  };
  export default CallDialog;
