import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import { HashRouter as Router, Route} from 'react-router-dom';

import routes from './MenuContent';
const Link = ScrollAnim.Link;



function Menu() {
  onFocus = (e) => {
    this.dom = e.target;


    // this.barAnimate();
    var idOn = document.getElementById(e.to)
    // if(e.target.index)

    idOn.className =  "pack-page " + e.to + " active"

  //  if(e.target.className(active) { this.setState({ selected: true }); console.log(idOn.className);}
    // this.state.selected


    // this.state.selected ? 'click-state' : 'base-state';
  }
  return (
    <Router>
      <div className="nav-wap">
        {routes.map((route) => (
          <Link className={route.classe} to={route.to} index={4} showHeightActive={['0%', '100%']} onFocus={this.onFocus}>
          {route.texto}
         </Link>
        ))}
      </div>
    </Router>
  );
}

export default Menu;
