import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import { HashRouter as Router, Route} from 'react-router-dom';

import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';

import content from './SectionsContent';


import 'rc-collapse/assets/index.css';
// import 'string.prototype.repeat';
import Collapse, { Panel } from 'rc-collapse';


require('rc-collapse/assets/index.css');


const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;


function Sections() {
    return (
      <div>
      <Test />
      <Conteudo />
      </div>
    )
  }



var collapse = (
  <Collapse accordion={true}>
    <Panel header="hello" headerClass="my-header-class">this is panel content</Panel>
    <Panel header="title2">this is panel content2 or other</Panel>
  </Collapse>
);

const text = `
  A dog is a type of domesticated animal.
  Known for its loyalty and faithfulness,
  it can be found as a welcome guest in many households across the world.
`;

function random() {
  return parseInt(Math.random() * 10, 10) + 1;
}

  class Conteudo extends React.Component {


    render() {




      return (
        <span>
          <Element className="pack-page page0" id="page0"
            style={{ backgroundColor: '#ffffff' }}
          >
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>
            </QueueAnim>
          </Element>

          {content.map((route) => ( 

            <ScrollOverPack
              id={route.id} className={"pack-page  "+ route.id}
                style={{ backgroundColor: {...route.conteudo[0]}.bg , 'z-index': route.zindex }}
                playScale={0.1}
                >



                <TweenOne 
                      className="tween-one" key="0"
                      animation={{  delay: 200, duration: 0, translateY: 0, opacity: 1 }}
                      style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50 }}
                      >            {console.log({...route.idnum}    )}
                          <h2>{{...route.conteudo[0]}.titulo} </h2>
                          <div>{{...route.conteudo[0]}.descricao}</div>
                      </TweenOne>

            </ScrollOverPack>
          ))}
        </span>
      )
  }
}


const arrowPath = 'M869 487.8L491.2 159.9c-2.9-2.5-6.6-3.9-10.5-3.9h-88' +
  '.5c-7.4 0-10.8 9.2-5.2 14l350.2 304H152c-4.4 0-8 3.6-8 8v60c0 4.4 3.' +
  '6 8 8 8h585.1L386.9 854c-5.6 4.9-2.2 14 5.2 14h91.5c1.9 0 3.8-0.7 5.' +
  '2-2L869 536.2c14.7-12.8 14.7-35.6 0-48.4z';

function expandIcon({ isActive }) {
  return (
    <i style={{ marginRight: '.5rem' }}>
      <svg
        viewBox="0 0 1024 1024"
        width="1em"
        height="1em"
        fill="currentColor"
        style={{
          verticalAlign: '-.125em',
          transition: 'transform .2s',
          transform: `rotate(${isActive ? 90 : 0}deg)`,
        }}
      >
        <path d={arrowPath} p-id="5827"></path>
      </svg>
    </i>
  );
}
class Test extends React.Component {
  state = {
    time: random(),
    accordion: false,
    activeKey: ['4'],
  }

  onChange = (activeKey) => {
    this.setState({
      activeKey,
    });
  }

  getItems() {
    const items = [];
    for (let i = 0, len = 3; i < len; i++) {
      const key = i + 1;
      items.push(
        <Panel header={`This is panel header ${key}`} key={key} disabled={i === 0}>
          <p>{text.repeat(this.state.time)}</p>
        </Panel>
      );
    }
    items.push(
      <Panel header={`This is panel header 4`} key="4">
        <Collapse defaultActiveKey="1" expandIcon={expandIcon}>
          <Panel header={`This is panel nest panel`} key="1" id="header-test">
            <p>{text}</p>
          </Panel>
        </Collapse>
      </Panel>
    );

    items.push(
      <Panel header={`This is panel header 5`} key="5">
        <Collapse defaultActiveKey="1">
          <Panel header={`This is panel nest panel`} key="1" id="another-test">
            <form>
              <label htmlFor="test">Name:&nbsp;</label>
              <input type="text" id="test" />
            </form>
          </Panel>
        </Collapse>
      </Panel>
    );

    return items;
  }

  setActivityKey = () => {
    this.setState({
      activeKey: ['2'],
    });
  }

  toggle = () => {
    this.setState({
      accordion: !this.state.accordion,
    });
  }

  reRender = () => {
    this.setState({
      time: random(),
    });
  }

  render() {
    const accordion = this.state.accordion;
    const btn = accordion ? 'Mode: accordion' : 'Mode: collapse';
    const activeKey = this.state.activeKey;
    return (<div style={{ margin: 20, width: 400 }}>
      <button onClick={this.reRender}>reRender</button>
      <button onClick={this.toggle}>{btn}</button>
      <br /><br />
      <button onClick={this.setActivityKey}>active header 2</button>
      <br /><br />
      <Collapse
        accordion={accordion}
        onChange={this.onChange}
        activeKey={activeKey}
        expandIcon={expandIcon}
      >
        {this.getItems()}
      </Collapse>
    </div>);
  }
}




export default Sections;
