import React from 'react';
import {HashRouter as Router, Route, Link} from "react-router-dom";
import Dialog from '@material-ui/core/Dialog';
import CloseIcon from '@material-ui/icons/Close';
import Tween from 'rc-tween-one';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import DialogTransition from './DialogTransition';
import content from './SectionsContent';
import CallDialog from './Dialog';
import { callbackify } from 'util';
import { Hidden } from '@material-ui/core';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);

function Transition(props) {
    return <Fade style={{width: '100vw'}} direction="down" {...props}/>;
}
class Dials extends React.Component {
    constructor({data, props}) {
                super(props);
                this.state = {
                    open: false,
                    reverse: true
                };
                this.i = find(data.params.i)
            }
            componentDidMount() {


            }
    handleClickOpen = () => {
                this.setState({open: true, 
                    reverse: false});
        };
            handleClose = () => {
                var dialog = document.getElementById('dialog');
                dialog.scrollTo(0,0)
                this.setState({reverse: true});
                setTimeout(() => {

                this.setState({open: false});
                }, 3000);
            };
    render() {
        return(

               <div>
                   {/* <ScrollOverPack always={true} appear={true} playScale={0.5}>
                   <Tween reverse={this.state.reverse} animation={{ delay: 3000, x: 0, ease: 'easeInOutExpo', opacity:0, duration: 1000}} style={{
                            top: 0,
                            left: 0,
                            position: 'fixed',
                            transform: 'translateX(-120px)', opacity: 1}}> */}
                     <Button
                        className="ver_mais"
                        onClick={this.handleClickOpen}
                        style={{
                            top: 0,
                            left: 0,
                        margin: 'calc(470 * ((100vw + 100vh) / 2 / 1120))  0 0 calc(550 * ((100vw + 100vh) / 2 / 1120))',
                        zIndex: 7,
                        cursor: 'pointer',
                        position: 'fixed',
                        height: 'calc(200 * ((100vw + 100vh) / 2 / 1120))',
                        width: 'calc(200 * ((100vw + 100vh) / 2 / 1120))'
                    }}>
                       <i className="material-icons ver_mais">
trending_flat
</i>
                    </Button>
                    {/* </Tween></ScrollOverPack> */}
                    <Dialog id='dialog' fullScreen 
                            TransitionComponent={Transition}
                        elevation={8} open={this.state.open} transitionDuration={{
                        enter: 800,
                        exit: 800
                    }} style={{
                        // backgroundColor: '#ffffff',
                        zIndex: 99999,
                        height: '100%',
                        width: '100vw',
                        position: 'absolute'
                    }}>
                        <CallDialog  m={{p: {id: this.i.id}}} reverse={this.state.reverse} />
                        <Tween reverse={this.state.reverse} reverseDelay={80} animation={[{delay: 1400, duration: 90, opacity: 0},{delay: 0, duration: 1000, opacity: 1, ease: 'easeInOutExpo'}]} style={{opacity: 0}}>
                        <Button
                            style={{
                            backgroundColor: 'transparent',
                            top: 0,
                            position: "fixed",
                            zIndex: 9999,
                            right: '1em'
                        }}
                            onClick={this.handleClose}>
                            <CloseIcon
                                style={{
                                width: 'calc((100vw+100vh)/2/14)',
                                height: 'calc((100vw+100vh)/2/14)',
                                backgroundColor: this.i.cor3,
                                color: '#fff',
                                borderRadius: '20%',
                                padding: '0.5em'
                            }}/>
                        </Button></Tween>
                        {DialogTransition({a: this.i, key: 'tweenOne3', reverse: this.state.reverse, reverseDelay: 0})}
                    </Dialog>
                    </div>

        )

    }
}
export default Dials;
