import ScrollAnim from 'rc-scroll-anim';

// var Scroll = require('react-scroll'); import ReactDOM from 'react-dom';
import React from 'react';
// import Button from '@material-ui/core/Button';
// import Dialog from '@material-ui/core/Dialog';
// import IconButton from '@material-ui/core/IconButton';
// import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import QueueAnim from 'rc-queue-anim';
import Tween from 'rc-tween-one';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
import Intro from './Intro';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;

ScrollAnim
    .scrollScreen
    .init({loop: false, duration: 700, ease: "linear", scrollInterval: 1400});

const find = id => content.find(p => p.id = id);

function Sections() {
    return (
            <Home/>
    )
}

class Conteudo extends React.Component {

    constructor({match, i}) {
        super(match)
        // var i = find(match.params.id)
    }
    render(match) {
        // var match;
        let i = find(match.params.id);
        return (
            <TweenOne
                            // show={this.state.show}
                            className={"tween-one cabecalho"}
                            key={i.id}
                            animation={{
                            delay: 5,
                            duration: 100,
                            opacity: 1
                        }}
                            style={{
                            opacity: 0
                        }}>
                            <h1 style={{color: this.state.i.color }}>{this.state.i.letra}</h1>
                            <label>{this.state.i.label}</label>
                            <h2>{this.state.i.h2}</h2>
                            <div className='descricao'>{this.state.i.descricao}</div>
                        </TweenOne>
        )
    }
}
function ScrollOver({ match }) {
    let i = find(match.params.id);
    return (
        <ScrollOverPack
        className={i.classe + i.id}
        id={i.hash + i.id}
        style={{
        backgroundColor: i.bg
    }}
        playScale={0.1}>
        <Conteudo match={{ params: { id: i.id }}} />
    </ScrollOverPack>
    )
}


class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
    }
    onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3400);
    }

    handleClickOpen = () => {
        this.setState({
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        })
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    };
    // componentDidMount() {     setTimeout(() => {         this.setState({show:
    // true});     }, 100);     window.addEventListener('resize',
    // this.handleWindowSizeChange.bind(this)); window.removeEventListener('resize',
    // this.handleWindowSizeChange.bind(this)); } handleWindowSizeChange = () => {
    // ScrollAnim         .scrollScreen   .init({loop: false, duration: 1200, ease:
    // "easeInOutQuart", scrollInterval: 800}); };
    handleClose = () => {
        this.setState({items: []});
        this.setState({paused: false, reverse: true, buttonCloseReverse: true, show: true});
        setTimeout(() => {
            this.setState({buttonEnterReverse: false, open: false, show: false});
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
        }, 3200);
    };
    render() {
        return (
            <div>
            <Intro/>    
            <ScrollOver match={{ params: { id: 1 }, url: "" }} />
            <ScrollOver match={{ params: { id: 2 }, url: "" }} />
            <ScrollOver match={{ params: { id: 3 }, url: "" }} />
            <ScrollOver match={{ params: { id: 4 }, url: "" }} />
            </div>
        );
    }
}

export default Sections;
