import ScrollAnim from 'rc-scroll-anim';
import Scroll from 'react-scroll';

// var Scroll = require('react-scroll'); import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import Tween from 'rc-tween-one';
import TweenOne from 'rc-tween-one';
import Animate from 'rc-animate';
import content from './SectionsContent';
import {Paper} from '@material-ui/core';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;

var scroll = Scroll.animateScroll;
var scrollSpy = Scroll.scrollSpy;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    }
};
function Transition(props) {
    return <Fade direction="up" {...props}/>;
}

function Sections() {
    return (
        <span >
            <Home/>
        </span>
    )
}
class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {

            show: false,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false,
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        };
    }

    handleClickOpen = () => {
        this.setState({reverse: false});
        this.setState({buttonEnterReverse: true});
        this.setState({buttonCloseReverse: false});
        this.setState({
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        })
        setTimeout(() => {
            this.setState({open: true});
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    };
    componentDidMount() {
        setTimeout(() => {
            this.setState({show: true});
        }, 100);
        window.addEventListener('resize', this.handleWindowSizeChange.bind(this));
        window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    }
    handleWindowSizeChange = () => {
        ScrollAnim
            .scrollScreen
            .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});

    };
    handleClose = () => {
        this.setState({items: []});
        this.setState({reverse: true});
        this.setState({buttonCloseReverse: true});
        setTimeout(() => {
            this.setState({open: false});
            this.setState({buttonEnterReverse: false});
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
        }, 2250);
    };
    render() {
        const classes = this.props;
        return (
            <div>
                <Link className="nav-list logo" onClick={this.handleClose}>
                    Alê Almeida
                </Link>
                {content.map(i => (

                    <ScrollOverPack
                        id={i.hash + i.id}
                        className={i.classe + i.id}
                        style={{
                        backgroundColor: i.bg,
                        zIndex: i.zindex
                    }}
                        playScale={0.1}>
                        <TweenOne
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 200,
                            duration: 0,
                            translateY: 0,
                            opacity: 1
                        }}
                            style={{
                            transform: 'translateY(0px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                        </TweenOne>
                        <Tween
                            className="tween"
                            reverse={this.state.buttonEnterReverse}
                            animation={[{
                                delay: 600,
                                duration: 600,
                                x: 0,
                                opacity: 1
                            }
                        ]}
                            style={{
                            transform: 'translateX(-10px)',
                            opacity: 0,
                            position: 'fixed',
                            zIndex: '9999',
                            justifyContent: 'flex-start',
                            top: '50%'
                        }}>

                            <Button
                                style={{
                                width: 'auto',
                                height: 'auto'
                            }}
                                onClick={this.handleClickOpen}>
                                Ver projeto
                            </Button>
                        </Tween>
                        <Dialog
                            fullScreen
                            id={'anchor' + i.id}
                            className="pack-page"
                            elevation={0}
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            transitionDuration={0} 
                            TransitionComponent={Transition}
                            style={{
                            backgroundColor: i.bg,
                            zIndex: 1500,
                            height: '100%'
                        }}>
                            <Button
                                to={"anchor" + i.id}
                                style={{
                                backgroundColor: 'transparent'
                            }}
                                color="inherit"
                                onClick={this.handleClose}>
                                <Tween
                                    reverseDelay={1600}
                                    reverse={this.state.buttonCloseReverse}
                                    animation={[{
                                        delay: 1800,
                                        duration: 200,
                                        rotate: 90,
                                        opacity: 1
                                    }
                                ]}
                                    style={{
                                    transform: 'rotate(0deg)',
                                    transformOrigin: '50%, 50%',
                                    width: '40px',
                                    height: '40px',
                                    position: 'fixed',
                                    opacity: 0,
                                    zIndex: '9999',
                                    textAlign: "center",
                                    top: '2em',
                                    right: '2em'
                                }}>
                                    <CloseIcon
                                        style={{
                                        width: '40px',
                                        height: '40px'
                                    }}/>
                                </Tween>
                            </Button>
                            <TweenOne
                                id={'anchor' + i.id}
                                className={"tween-one"}
                                key="0"
                                animation={{
                                delay: 800,
                                duration: [
                                    200, 100
                                ],
                                translateY: 0,
                                color: '#fff',
                                bezier: 1
                            }}
                                style={{
                                transform: 'translateY(0px)',
                                zIndex: 1
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                                <QueueAnim
                                    type={['right', 'right']}
                                    interval={[100, 200]}
                                    delay={[2400, 100]}
                                    duration={[900, 1000]}
                                    ease={['easeOutBack', 'easeInOutCirc']}
                                    leaveReverse>
                                    {this
                                        .state
                                        .items
                                        .map((item) => <div key={item.key}>
                                            <p>A scrollScreen demo</p>
                                            {/* {item.children} */}
                                        </div>)}
                                </QueueAnim>
                            </TweenOne>
                            <TweenOne
                                className={"tween-one"}
                                key="0"
                                reverse={true}
                                animation={{
                                delay: 600,
                                duration: [200],
                                translateY: 0,
                                color: '#fff',
                                bezier: 1
                            }}
                                style={{
                                transform: 'translateY(1000px)',
                                zIndex: 1
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                            </TweenOne>
                            <Tween
                                reverse={this.state.reverse}
                                reverseDelay={700}
                                animation={{
                                y: '99.8vh',
                                width: '100vw',
                                height: '100vh',
                                delay: 0,
                                bottom: '0',
                                duration: 2000,
                                ease: 'easeInOutQuart'
                            }}
                                style={{
                                transform: 'translateY("-100vh")',
                                backgroundColor: '#222',
                                height: '400vh',
                                bottom: '100%',
                                position: "fixed",
                                width: '100vw',
                                zIndex: 1
                            }}
                                key="t"/>
                        </Dialog>
                    </ScrollOverPack>
                ))}
            </div>
        );
    }
}
function Intro({match}) {

    return (

        <Element
            className="pack-page page0"
            id='page0'
            style={{
            backgroundColor: '#ffffff',
            zIndex: 1500
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'alpha']}
                className="intro"
                interval={[200, 700]}
                delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na
                    <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                </div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
}
export default Sections;
