import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import './assets/styles/main.css';
import Menu from './components/ui/Menu';
import Sections from './components/ui/Sections';

const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;

ScrollAnim.scrollScreen.init({ loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800 });

class Demo extends React.Component {
  constructor() {
    super();
    this.state = {
      show: false,
    };
  }

  render() {
    return (
      <div className="container">
      <div className="nav">
          <Menu />
      </div>
      <Element id="page0" index={0} className="pack-page page0">
      <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
          duration={[800, 500]}
          always={false}>
        <div key='a'>Olá!</div>
        <div key='b'>Sou Alexandre Almeida,</div>
        <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
        <div key='d'></div>
        <div key='e'>São Paulo.</div>
      </QueueAnim>
      </Element>
        <ScrollOverPack
          id="page1" index={1}
          className="pack-page page1"
          ease="linear"
          location="page1"
          style={{ backgroundColor: '#f0f0f0' }}
          playScale={0.1}
        >
          <TweenOne 
          className="tween-one" key="0"
          animation={{  delay: 1, duration: 0, translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Pop</h2>
            <div>Mini Pop Credicard</div>
          </TweenOne>
        </ScrollOverPack>
      <ScrollOverPack
        id="page2"
        index={2}
        className="pack-page page2"
        style={{ backgroundColor: '#ccc' }}
        ease="linear"
        playScale={0.1}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Caixa</h2>
            <div>Nova Caixa</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page3"
        id="page3"
        index={3}
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.1}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Vivo</h2>
            <div>Novo Vivo Easy</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page4"
        id="page4"
        index={4}
        style={{ backgroundColor: '#ffffff' }}
        ease="linear"
        playScale={0.1}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>RB</h2>
            <div>Novo Reckitt</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page5"
        id="page5"
        index={5}
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.1}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Nacional Gás</h2>
            <div>Nacional Gás</div>
          </TweenOne>
      </ScrollOverPack>
    </div>

    );

  }

}

ReactDOM.render([<Demo />], document.getElementById('root'));

