import React from 'react';
import { HashRouter as Router, Switch, Route } from "react-router-dom";
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
const Link = ScrollAnim.Link;
const EventListener = ScrollAnim.Event;
const find = id => content.find(p => p.id === id);

const ChamaMenu = ({data, onFocus, onBlur}) => {
    let n = find(data.params.id);
    return(

    <Link
        className="nav-list"
        to={n.hash}
        toShowHeight
        onFocus={this.onFocus}
        onBlur={this.onBlur}
        >
        {'Example'+ n.id}
    </Link>
    )
}

class MontaMenu extends React.Component {

    componentDidMount() {
        ScrollAnim.scrollScreen.init({ loop: true });
      }
      refreshPage = () => {
            setTimeout(() => {
                window.scrollTo(0, 0);
            }, 100);
      }
      onFocus = (e) => {
        this.dom = e.target;
        // this.barAnimate();
        e.target.className = 'active'
        document.getElementById(e.to).setAttribute('class','pack-page '+e.to+' active')
        console.log(e.to);
      }

      onBlur = (e) => {
        console.log(e.to, 'blur');
        document.getElementById(e.to).setAttribute('class','pack-page '+e.to)
      }

      onChange = (e) => {
        console.log(e);
      }

      barAnimate = () => {
        if (!this.dom) {
          return;
        }
        const bar = this.bar; 
        console.log(bar);
        bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
      }

render(){
    return (
        <nav className='nav'>
        <div className='nav-wap'>
        {
            content.map(i => (
                <Switch>
           >
           <ChamaMenu exact path="/" onFocus={this.onFocus} onBlur={this.onBlur} data={{params: {id: i.id} }} />
        </Switch>
            ))
        }
        </div>
        </nav>
    )

}
}
function Menu() {
    return (
      <Router>
        <Route component={MontaMenu} />
      </Router>
    );
  }
