import ScrollAnim from 'rc-scroll-anim';

import PropTypes from 'prop-types';
// import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import Grow from '@material-ui/core/Grow';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    }
};

function Transition(props) {
    return <Slide direction="up" {...props}/>;
}

function Sections() {
    return (
        <span >
            <Home/>
        </span>
    )
}
class Home extends React.Component {

    state = {
        open: false,
        checked: false
    };
    handleClickOpen = () => {
        this.setState({open: true});

        ScrollAnim
            .scrollScreen
            .unMount();

    };
    handleClose = () => {
        this.setState({open: false});

        ScrollAnim
            .scrollScreen
            .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    };
    handleChange = () => {
        this.setState(state => ({
            checked: !state.checked
        }));
    };
    render() {
        const {classes} = this.props;
        const {checked} = this.state;
        return (
            <div>
                <Intro
                    match={{
                    params: {
                        id: 0
                    },
                    titulo: 'Intro'
                }}/> {content.map(i => (
                    <ScrollOverPack
                        id={i.hash + i.id}
                        className={i.classe + i.id}
                        style={{
                        backgroundColor: i.bg,
                        zIndex: i.zindex
                    }}
                        playScale={0.1}>
                        <TweenOne
                            className={"tween-one tw"+i.id}
                            key="0"
                            animation={{
                            delay: 200,
                            duration: 0,
                            translateY: 0,
                            opacity: 1
                        }}
                            style={{
                            transform: 'translateY(0px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                            <Button
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em'
                            }}
                                onClick={this.handleClickOpen}>
                                Open full - screen dialog
                            </Button>
                        </TweenOne>
                        <Dialog
                            fullScreen
                            TransitionComponent={Transition}
                            transitionDuration={400}
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            style={{
                            background: 'transparent',
                            zIndex: 9   
                        }}>
                            <IconButton
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em',
                                backgroundColor: '#f0f0f0'
                            }}
                                color="inherit"
                                onClick={this.handleClose}>
                                <CloseIcon/>
                            </IconButton>
                            <TweenOne
                                className={"tween-one"}
                                key="0"
                                animation={{
                                delay: 220,
                                duration: 60,
                                translateY: 0,
                                opacity: 1
                            }}
                                style={{
                                transform: 'translateY(-10px)',
                                opacity: 0
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                            </TweenOne>
                        </Dialog>
                    </ScrollOverPack>
                ))}
            </div>
        );
    }
}
function Intro({match}) {
    let data = find(match.params.id);
    return (

        <Element
            className="pack-page page0"
            id='page0'
            style={{
            backgroundColor: '#ffffff'
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'alpha']}
                className="intro"
                interval={[200, 700]}
                delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na
                    <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                </div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
}
Home.propTypes = {
    classes: PropTypes.object.isRequired
};
export default Sections;
