import React from 'react';
import {HashRouter as Router, Route, Link} from "react-router-dom";
import Dialog from '@material-ui/core/Dialog';
import Slide from '@material-ui/core/Slide';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import content from './SectionsContent';
import CallDialog from './Dialog';

// const find = id => content.find(p => p.id === id);

class Dials extends React.Component {
    handleClickOpen = () => {
            // this.setState({reverse: false});
            // setTimeout(() => {
                this.setState({open: true});
            // }, 1000);
        };
    render() {
        return(
            content.map(i => (
                <div
                id={'dial'+i.id}
                        style={{ position: 'fixed', height: '100vh',top: 0, zIndex: 1800 }}
                >
                     <Button
                        className="ver_mais"
                        onClick={this.handleClickOpen}
                        style={{
                        height: '4em',
                        left: 0,
                        top: 0,
                        zIndex: 999999,
                        cursor: 'pointer',
                        position: 'absolute'
                    }}>
                        {'ver projeto'}
                    </Button>
                    <Dialog fullScreen 
                        elevation={8} open={this.state.open} transitionDuration={{
                        enter: 0,
                        exit: 0
                    }} style={{
                        backgroundColor: '#ffffff',
                        zIndex: 99999,
                        height: '100%',
                        position: 'absolute'
                    }}>
                        <CallDialog m={{p: {id: i.id}}} />
                    </Dialog>

                </div>

            ))
        )

    }
}
export default Dials;
