import React from 'react';
import { HashRouter as Router, Route } from "react-router-dom";
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
const Link = ScrollAnim.Link;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: true });
const find = (id) => content.find(p => p.id === id);

const Menu = ({d}) => {
    return (
    <Router id={{d}}>
        <Route  component={MontaMenu} />
    </Router>)
}

class MontaMenu extends React.Component {

    componentDidMount() {
        EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
      }

      onFocus = (e) => {
        this.dom = e.target;
        this.barAnimate();
        e.target.className = 'active'
        document.getElementById(e.to).setAttribute('class','pack-page active')
        console.log(e.to);
      }

      onBlur = (e) => {
        console.log(e.to, 'blur');
        document.getElementById(e.to).setAttribute('class','pack-page')
      }

      onChange = (e) => {
        console.log(e);
      }

      barAnimate = () => {
        if (!this.dom) {
          return;
        }
        const bar = this.bar; 
        console.log(bar);
        bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
      }

render(){
    let i = this.i;
    return (
        <nav className='nav'>
        <div className='nav-wap'>
        {
            content.map(i => (
                <Link
                    d={{p: {id: i.id}}}
                    className="nav-list"
                    key={i.id}
                    to={i.hash}
                    toShowHeight
                    onFocus={this.onFocus}
                    onBlur={this.onBlur}
                    >
                    {'Example'+ i.id}
                </Link>
            ))
        }
        <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
        </nav>
    )

}
}

