    this.dom = e.target;
    this.barAnimate();

    var idOn = document.getElementById(e.to)
    idOn.className =  "pack-page " + e.to + " active"
  }
    handleWindowSizeChange = () => {
        ScrollAnim.scrollScreen.unMount();
        ScrollAnim.scrollScreen.init({ loop: false, duration: 1500, ease: "easeInOutQuart", scrollInterval: 800 });
      };

