import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const EventListener = ScrollAnim.Event;

class Menu extends React.Component {
    constructor(props) {
        super(props);
    }
    componentDidMount() {
        EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
        window.addEventListener('resize', this.handleWindowSizeChange.bind(this));
        window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    }
    onFocus = (e) => {
        this.dom = e.target;
        this.barAnimate();
        var idOn = document.getElementById(e.to)
        // if(e.target.index)
        idOn.className = "pack-page " + e.to + " active"
    }
    onBlur = (e) => {
        var idOn = document.getElementById(e.to);
        idOn.className = "pack-page " + e.to;
    }
    barAnimate = () => {
        if (!this.dom) {
            return;
        }
        const bar = this.bar;
        bar.style.left = `$ {this
            .dom
            .getBoundingClientRect()
.left}px`;
    }
    render() {
        return (
            <div className="container">

                <div className="nav">
                    <div className="nav-wap">
                        <Link
                            className="nav-list logo"
                            to={"page0"}
                            toShowHeight
                            onFocus={this.onFocus}
                            onBlur={this.onBlur}>
                            AlÃª Almeida
                        </Link>
                        {content.map(i => (
                            <Link
                                className="nav-list"
                                to={"page" + i.id}
                                toShowHeight
                                onFocus={this.onFocus}
                                onBlur={this.onBlur}>
                                Example2
                            </Link>
                        ))}
                        <div ref= {(c) => { this.bar = c; } } className="nav-bar"/>
                    </div>
                </div>
            </div>
        );
    }
}

export default Menu;
