import ScrollAnim from 'rc-scroll-anim';
// import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = theme => ({
    root: {
      height: 180,
    },
    wrapper: {
      width: 100 + theme.spacing.unit * 2,
    },
    paper: {
      zIndex: 1,
      position: 'relative',
      margin: theme.spacing.unit,
    },
    svg: {
      width: 100,
      height: 100,
    },
    polygon: {
      fill: theme.palette.common.white,
      stroke: theme.palette.divider,
      strokeWidth: 1,
    },
  });

function Transition(props) {
    return (<Fade in={true} { ...props }/>)
}

function Sections() {
    return (
        <span >
            <Home/>
        </span>
    )
}
class Home extends React.Component {
    state = {
        open: false
    };
    handleClickOpen = () => {
        this.setState({open: true});
        ScrollAnim
            .scrollScreen
            .unMount();
    };
    handleClose = () => {
        this.setState({open: false});
        ScrollAnim
            .scrollScreen
            .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
    };
    render() {
        const classes = this.props;
        return (
            <div>
                <Intro
                    match={{
                    params: {
                        id: 0
                    },
                    titulo: 'Intro'
                }}/> {content.map(i => (
                    <ScrollOverPack
                        id={i.hash + i.id}
                        className={i.classe + i.id}
                        style={{
                        backgroundColor: i.bg,
                        zIndex: i.zindex
                    }}
                        playScale={0.1}>
                        <TweenOne
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 200,
                            duration: 0,
                            translateY: 0,
                            opacity: 1
                        }}
                            style={{
                            transform: 'translateY(0px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                            <Button
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em'
                            }}
                                onClick={this.handleClickOpen}>
                                Open full - screen dialog
                            </Button>
                        </TweenOne>
                        <Dialog className={classes.root}
                            fullScreen
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            onRequestClose={this.handleRequestClose}
                            enterTransitionDuration={10}
                            leaveTransitionDuration={1000}
                            transitionComponent={Transition}
                            style={{
                            backgroundColor: '#dddddd'
                        }}>
                            <IconButton
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em',
                                backgroundColor: '#f0f0f0'
                            }}
                                color="inherit"
                                onClick={this.handleClose}>
                                <CloseIcon/>
                            </IconButton>
                            <TweenOne
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 0,
                            duration: 0,
                            translateY: 0
                        }}
                            style={{
                            transform: 'translateY(0px)'
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                        </TweenOne>
                        <TweenOne
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 100,
                            duration:1000,
                            translateY: 300,
                            opacity:1 
                        }}
                            style={{
                            transform: 'translateY(350px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                        </TweenOne>
                        </Dialog>
                    </ScrollOverPack>
                ))}
            </div>
        );
    }
}
Home.propTypes = {
    classes: PropTypes.object.isRequired,
  };
function Intro({match}) {
    let data = find(match.params.id);
    return (

        <Element
            className="pack-page page0"
            id='page0'
            style={{
            backgroundColor: '#ffffff'
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'alpha']}
                className="intro"
                interval={[200, 700]}
                delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na
                    <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                </div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
}
export default Sections;
