import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
const Link = ScrollAnim.Link;

const Menu = () => {
    return <LoadMenu/>
}

class LoadMenu extends React.Component {
    onFocus = (e) => {
        this.dom = e.target;
        // this.barAnimate();
        e.target.className = 'active';
        document
            .getElementById(e.to)
            .setAttribute('class', 'pack-page ' + e.to + ' active');
    }

    onBlur = (e) => {
        console.log(e.to, 'blur');
        document
            .getElementById(e.to)
            .setAttribute('class', 'pack-page ' + e.to);
    }

    onChange = (e) => {
        console.log(e);
    }

    render() {
        return (
            <nav className='nav'>
                <div className='nav-wap'>
                    {content.map(i => (
                        <Link
                            className="nav-list"
                            to={i.hash}
                            toShowHeight
                            onFocus={this.onFocus}
                            onBlur={this.onBlur}
                            m={{
                            p: {
                                u: i.id
                            }
                        }}>
                            {'Example' + i.id}
                        </Link>
                    ))
}
                </div>
            </nav>
        )

    }
}
