import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import { HashRouter as Router, Route, Switch} from 'react-router-dom';

import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import AriaModal from '../../react-aria-modal';
import FocusTrap from 'focus-trap-react';

import content from './SectionsContent';

// const FocusTrap = require('focus-trap-react');
// const AriaModal = require('../../react-aria-modal');

//ScrollAnim.scrollScreen.unMount({ });


const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;


function find(id) {
  return content.find(p => p.id ===id);
}
function Sections() {
    return  ( 
      <span>
            <Intro match={{ params: { id: 0}, titulo: 'Intro' }}  component={Modal} />
            <Conteudo match={{ params: { id: 1, titulo: 'Pop'} }}  component={Modal[1]} />
            <Conteudo match={{ params: { id: 2, titulo: 'Vivo'} }}  component={Modal[2]} />
            <Conteudo match={{ params: { id: 3, titulo: 'Pop'} }}  component={Modal[3]} />
            <Conteudo match={{ params: { id: 4, titulo: 'Diageo'} }}  component={Modal[4]}  />
      </span>

    )
  }

  // class Modal extends React.Component {

  //   render() {
  //     let { location } = this.props;

  //     let isModal = !!(
  //       location.state &&
  //       location.state.modal &&
  //       this.previousLocation !== location
  //     ); // not initial render

  //     return (
  //       <div>
  //         <Switch location={isModal ? this.previousLocation : location}>
  //           <Route exact path="/" component={Intro}  />
  //           <Route path="/gallery" component={Conteudo} />
  //         </Switch>
  //         {isModal ? <Conteudo  component={Modal} /> : null}
  //       </div>
  //     );
  //   }

  // }
function Intro({ match }) {
    let data = find(match.params.id);
    return (

        <Element className="pack-page page0" id={data.id} 
            style={{ backgroundColor: '#ffffff' }}
          >
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>
            </QueueAnim>
        </Element>
    );
  }
  function Conteudo({ match }) {
    let data = find(match.params.id);



    return (

      <span>
      <ScrollOverPack id={ data.hash + data.id }   className={ data.classe + data.id  } style={{ backgroundColor: data.bg , 'z-index': data.zindex }}
            playScale={0.1} >
        <TweenOne className={"tween-one conteudo-colapsado"} key="0" 
              animation={{  delay: 200, duration: 0, translateY: 0, opacity: 1 }}
              style={{ transform: 'translateY(0px)', opacity: 0 }} >          
                 <h2>{data.h2}</h2>
                 <div>{data.descricao}</div>

                          <Link to="/img/2">Tomato</Link>
                          <a onClick={this.activateModal} >hahaha</a>
                          {/* <Modal contId={data.id} /> */}
                      </TweenOne>
            </ScrollOverPack>

          </span>
    );
  }
  class Modal extends React.Component {
    constructor(props) {
      super(props);

      const idsa = props.contId;


      this.state = {
        modalActive: false
      };

      this.activateModal = this.activateModal.bind(this);
      this.deactivateModal = this.deactivateModal.bind(this);
      this.getApplicationNode = this.getApplicationNode.bind(this);
    }

    activateModal = (e) => {
      this.setState({ modalActive: true });



      var classOn = document.getElementsByClassName('pack-page active');
      var idOn = document.getElementById(classOn);
      {console.log(idsa) }
    };

    deactivateModal = () => {
      this.setState({ modalActive: false });
    };

    getApplicationNode = () => {
      return document.getElementById('application');
    };


  render() {
    const modal = this.state.modalActive
      ?

      <div>


       <AriaModal
            titleText="demo three"
            onExit={this.deactivateModal}
            focusDialog={true}
            escapeExits={false}
            underlayStyle={{ padding: '0' }}
            aria-describedby='describer'
            data-test-id='test-id'
          >
            <div id="demo-three-modal" className="modal" style={{ background: "#f0f0f0"}}>
            <div className="nav">
                  <div className="nav-wap">
                      <Link className="nav-list logo" to={"page"} showHeightActive={['50%', '10%']}
                    >
                      Alê Almeida
                    </Link>
                    </div>
                  </div>
              <div className="modal-body">

                <Link className="close" style={{ position: 'fixed', right: '1em', top: '1em' }} onClick={this.deactivateModal}>
                  X
                </Link>

              <div className={"pack-page"}>
                <div className="tween-one">
                  <h2>Pop </h2>
                  <div>auihsiuh</div>
                <p>
                  Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt
                  ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco
                  laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in
                  voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat
                  cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </p>
              </div>
              </div>
              </div>
            </div>
          </AriaModal>
          </div>

: false;

  return (
    <div>
        <button onClick={this.activateModal}>
          activate modal
        </button>

      {modal}
      </div>


  );
}
}


