import ScrollAnim from 'rc-scroll-anim';
// import ReactDOM from 'react-dom';
import React from 'react';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import IconButton from '@material-ui/core/IconButton';
import CloseIcon from '@material-ui/icons/Close';
import Slide from '@material-ui/core/Slide';
import Zoom from '@material-ui/core/Zoom';
import Fade from '@material-ui/core/Fade';
import {withStyles} from '@material-ui/core/styles';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import Animate from 'rc-animate';
import content from './SectionsContent';
import {Paper} from '@material-ui/core';
const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;

function find(id) {
    return content.find(p => p.id === id);
}
const styles = {
    appBar: {
        position: 'relative'
    },
    flex: {
        flex: 1
    }
};

function Transition(props) {
    return (<Fade in={true} { ...props }/>)
}

function Sections() {
    return (
        <span >
            <Home/>
        </span>
    )
}
class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            reverse: false,
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        };
    }
    state = {
        open: false
    };
    handleClickOpen = () => {
        this.setState({open: true, reverse: false});
        this.setState({items: [
            {
                key: 1
            }, {
                key: 2
            }, {
                key: 3
            }, {
                key: 4
            }, {
                key: 5
            }, {
                key: 6
            }
        ]})
        ScrollAnim
            .scrollScreen
            .unMount();
    };
    handleClose = () => {
        this.setState({items: []});
        this.setState({reverse: true});
        setTimeout(() => {
            this.setState({open: false});
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
        }, 4500);
    };
    render() {
        const classes = this.props;
        return (
            <div>
                <Link className="nav-list logo" offsetTop={100} onClick={this.handleClose}>
                    Alê Almeida
                </Link>
                {content.map(i => (

                    <ScrollOverPack
                        id={i.hash + i.id}
                        className={i.classe + i.id}
                        style={{
                        backgroundColor: i.bg,
                        zIndex: i.zindex
                    }}
                        playScale={0.1}>
                        <TweenOne
                            className={"tween-one"}
                            key="0"
                            animation={{
                            delay: 200,
                            duration: 0,
                            translateY: 0,
                            opacity: 1
                        }}
                            style={{
                            transform: 'translateY(0px)',
                            opacity: 0
                        }}>
                            <h2>{i.h2}</h2>
                            <div>{i.descricao}</div>
                            <Button
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em'
                            }}
                                onClick={this.handleClickOpen}>
                                Open full - screen dialog
                            </Button>
                        </TweenOne>
                        <Dialog
                            fullScreen
                            scroll={this.state.scroll}
                            open={this.state.open}
                            onClose={this.handleClose}
                            transitionDuration={0}
                            transitionComponent={Transition}
                            style={{
                            backgroundColor: '#dddddd',
                            zIndex: 999999
                        }}>
                            <Link
                                to={'anchor1'}
                                style={{
                                position: 'fixed',
                                zIndex: '9999',
                                top: '1em',
                                right: '1em',
                                backgroundColor: '#f0f0f0'
                            }}
                                color="inherit"
                                onClick={this.handleClose}>
                                <CloseIcon/>
                            </Link>
                            <TweenOne
                                id={'anchor' + i.id}
                                className={"tween-one"}
                                key="0"
                                reverse={true}
                                animation={{
                                delay: 600,
                                duration: [
                                    200, 100
                                ],
                                translateY: 0,
                                color: '#fff',
                                bezier: 1
                            }}
                                style={{
                                transform: 'translateY(0px)',
                                zIndex: 1
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                                <QueueAnim
                                    type={['right', 'left']}
                                    interval={[100, 200]}
                                    delay={[1000, 100]}
                                    duration={[500, 2000]}
                                    ease={['easeOutBack', 'easeInOutCirc']}
                                    leaveReverse
                                    reverse={this.state.reverse}>
                                    {this
                                        .state
                                        .items
                                        .map((item) => <div key={item.key}>
                                            <p>A scrollScreen demo</p>
                                            {/* {item.children} */}
                                        </div>)}
                                </QueueAnim>
                            </TweenOne>
                            <TweenOne
                                className={"tween-one"}
                                key="0"
                                reverse={true}
                                animation={{
                                delay: 600,
                                duration: [
                                    200
                                ],
                                translateY: 0,
                                color: '#fff',
                                bezier: 1
                            }}
                                style={{
                                transform: 'translateY(1000px)',
                                zIndex: 1
                            }}>
                                <h2>{i.h2}</h2>
                                <div>{i.descricao}</div>
                            </TweenOne>
                            <TweenOne
                                reverse={this.state.reverse}
                                reverseDelay={4000}
                                animation={{
                                y: 0,
                                width: '100vw',
                                height: '85vh',
                                delay: 300,
                                duration: 1000,
                                opacity: 1,
                                top: 0,
                                left: 0,
                                ease: 'easeInOutExpo'
                            }}
                                style={{
                                transform: 'translateY(0px)',
                                opacity: 1,
                                backgroundColor: '#562525',
                                height: '10vh',
                                top: 0,
                                left: '20vw',
                                position: "absolute",
                                width: '0vw',
                                zIndex: -0
                            }}
                                key="t"/>
                        </Dialog>
                    </ScrollOverPack>
                ))}
            </div>
        );
    }
}
function Intro({match}) {

    return (

        <Element
            className="pack-page page0"
            id='page0'
            style={{
            backgroundColor: '#ffffff',
            zIndex: 1500
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'alpha']}
                className="intro"
                interval={[200, 700]}
                delay={800}
                duration={[800, 500]}
                playScale={0.1}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na
                    <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,
                </div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>

            </QueueAnim>
        </Element>
    );
}
export default Sections;
