import React from 'react';
import TweenOne from 'rc-tween-one';

function DialogTransition({n}) {
    const styles = {
        width: '100%',
        height: '100vh',
        position: 'fixed',
        backgroundColor: n.cor1,
        opacity: 1,
        top: 0,
        zIndex: 9,
        transform: 'scale(0)'
    }
    const animation = {
        from: 
            {
                delay: 0,
                        duration: 800,
                        opacity: 1,
                        top: 0,
                        ease: "easeInQuad",
                        backgroundColor: n.cor1,
                        type: 'from',
                        transform: 'scale(0)'
            }
        ,
        to: 
            {
                delay: 2900,
                        duration: 2500,
                        opacity: 1,
                        top: 0,
                        ease: "easeOutExpo",
                        backgroundColor: n.cor2,
                        type: 'to',
                        transform: 'scale(1)'
            }

    };
    return (
        <TweenOne
        key="tweenOne"
                animation={
                [animation.from, animation.to]
            }
                style={styles}/>
    )
}

export default DialogTransition;
