import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import TweenOne from 'rc-tween-one';
import content from './SectionsContent';
import iniciaScroll from './Scroll';
import Twn from './Twn';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);

const OverPack =({data}) => {
    let n = find(data.params.id);
    const styles = {

                width: '100%',
                height: '600vh',
                position: 'fixed',
                transform: 'translateY(0vh)',
                backgroundColor: n.cor1,
                opacity: 1,
                top: '0vh',
                zIndex: 9
    }
    const animation = {
        from: [
            {
                delay: 0,
                duration: 1000,
                opacity: 1,
                top: '100vh',
                height: '0vh',
                ease: "easeOutCubic",
                backgroundColor: n.cor1,
                y: '0',
                type: 'from'
            }
        ],
        to: [
            {
                delay: 2900,
                duration: 2500,
                opacity: 1,
                top: '-100vh',
                ease: "easeInOutExpo",
                height: '100vh',
                backgroundColor: n.cor2,
                y: '0',
                type: 'to'
            }
        ]
    };
    return (
            <ScrollOverPack
                always={true}
                className={n.classe + n.id}
                id={n.hash + n.id}
                playScale={0.5}>
                    {Twn({n: n})} 
                <TweenOne
                    key="tweenOne"
                    animation={[...animation.from, ...animation.to]}
                    style={[...animation.styles]}
                    />
            </ScrollOverPack>)
}
class Home extends React.Component {
    componentDidMount(){
        iniciaScroll();
    }
    render() {
        return (content.map((i) => (<OverPack
            data={{
            params: {
                id: i.id
            }
        }}/>)));
    }
}

export default Home;
