import ScrollAnim from 'rc-scroll-anim';

// var Scroll = require('react-scroll');
import React from 'react';
// import Button from '@material-ui/core/Button';
// import Dialog from '@material-ui/core/Dialog';
// import IconButton from '@material-ui/core/IconButton';
// import CloseIcon from '@material-ui/icons/Close';
import Tween from 'rc-tween-one';
import content from './SectionsContent';
import Intro from './Intro';
const ScrollOverPack = ScrollAnim.OverPack;

ScrollAnim
    .scrollScreen
    .init({loop: false, duration: 900, ease: "easeInOutQuart", scrollInterval: 1400});

const find = id => content.find(p => p.id === id);

function Sections() {
    return (
            <Home/>
    )
}

function Conteudo({data}) {
    let n = find(data.params.id)
    return (
            <Tween
                            // show={this.state.show}
                            className={"tween-one cabecalho"}
                            key={n.id}
                            animation={{
                            delay: 0,
                            duration: 1000,
                            opacity: 1,
                            y: 0
                        }}
                            style={{
                            transform: 'translateY(-100vh)',
                            opacity: 0
                        }}>
                            <h1 style={{color: n.cor }}>{n.letra}</h1>
                            <label>{n.label}</label>
                            <h2>{n.h2}</h2>
                            <div className='descricao'>{n.descricao}</div>
                        </Tween>
       )
                       }
function ScrollOver({ match }) {
    let i = find(match.params.id);
    const n = i
    return (
        <ScrollOverPack
        className={i.classe + i.id}
        id={i.hash + i.id}
        style={{
        backgroundColor: i.bg,
        top: 0,
        zIndex: 9999
    }}
        playScale={0.1}>
        <Conteudo data={{params: n}} />
    </ScrollOverPack>
    )
}


class Home extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            paused: true,
            show: true,
            open: false,
            reverse: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };
    }
    onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3400);
    }

    handleClickOpen = () => {
        this.setState({
            items: [
                {
                    key: 1
                }, {
                    key: 2
                }, {
                    key: 3
                }, {
                    key: 4
                }, {
                    key: 5
                }, {
                    key: 6
                }
            ]
        })
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    };
    // componentDidMount() {     setTimeout(() => {         this.setState({show:
    // true});     }, 100);     window.addEventListener('resize',
    // this.handleWindowSizeChange.bind(this)); window.removeEventListener('resize',
    // this.handleWindowSizeChange.bind(this)); } handleWindowSizeChange = () => {
    // ScrollAnim         .scrollScreen   .init({loop: false, duration: 1200, ease:
    // "easeInOutQuart", scrollInterval: 800}); };
    handleClose = () => {
        this.setState({items: []});
        this.setState({paused: false, reverse: true, buttonCloseReverse: true, show: true});
        setTimeout(() => {
            this.setState({buttonEnterReverse: false, open: false, show: false});
            ScrollAnim
                .scrollScreen
                .init({loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 800});
        }, 3200);
    };
    render() {
        return (
            <div>
            <Intro/>   
            <ScrollOver match={{ params: { id: 1 } }} />
            <ScrollOver match={{ params: { id: 2 } }} />
            <ScrollOver match={{ params: { id: 3 } }} />
            <ScrollOver match={{ params: { id: 4 } }} />
            </div>
        );
    }
}

export default Sections;
