import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import QueueAnim from 'rc-queue-anim';
import intro from './IntroContent';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => intro.find(p => p.id === id);

const Intro = ({data}) => {
    let n = find(data.params.id);
    return (
        <ScrollOverPack always={true} className='pack-page page0' id='page0' data='dial0'>
            <QueueAnim
                key='queueAnim'
                type={['right', 'left']}
                className="intro"
                interval={[200, 800]}
                delay={[600, 500]}
                duration={[1000, 1500]}
                forcedReplay={true}
                leaveReverse={true}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alê Almeida,</div>
                <div key='c'>UX Designer,</div>
                <div key='d'>São Paulo.</div>
            </QueueAnim>
        </ScrollOverPack>
    )
}
export default Intro;
