import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import content from './SectionsContent';
import iniciaScroll from './Scroll';
import Twn from './Twn';
import Dial from './Dialog';
import Transition from './Transition';
const ScrollOverPack = ScrollAnim.OverPack;


const find = id => content.find(p => p.id === id);

const OverPack = ({data}) => {
    let n = find(data.params.id);

    return (
        <ScrollOverPack
            always={true}
            appear={true}
                componentProps={{ tweenOne2:  {reverse :true }, tweenOne:  {reverse :true } }}
            className={n.classe}
            id={n.hash}
            playScale={0.5}>

            {Twn({n: n})}
            {Transition({n: n})}
        </ScrollOverPack>
    )
}
class Home extends React.Component {
    constructor(props) {
    super(props);
    this.state = {

        paused: true,
        show: true,
        open: false,
        reverse: false,
        buttonEnterReverse: false,
        buttonCloseReverse: false
    };
}
    componentDidMount() {
        iniciaScroll();
    }onPlay = () => {
        this.setState({paused: false, reverse: false, buttonEnterReverse: true, buttonCloseReverse: false});
        setTimeout(() => {
            this.handleClickOpen()
            this.setState({open: true});
        }, 3400);
    }

    handleClickOpen = () => {
        setTimeout(() => {
            ScrollAnim
                .scrollScreen
                .unMount();
        }, 1000);
    };
    render() {

        return (
            content.map((i) => (<OverPack
            data={{
            params: {
                id: i.id
            }
        }}/>)));
    }
}

export default Home;
