import React from 'react';
import ScrollAnim from 'rc-scroll-anim';
import QueueAnim from 'rc-queue-anim';
import intro from './IntroContent';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => intro.find(p => p.id === id);

const Intro = ({data}) => {
    let n = find(data.params.id);

    return (
        <ScrollOverPack
            always={true}
            className={n.classe}
            id={n.hash}
            style={{
        backgroundColor: n.cor
        }}>
            <QueueAnim
                key='queueAnim'
                type={['right', 'left']}
                className={n.classe_child}
                interval={[200, 800]}
                delay={[600, 500]}
                duration={[1000, 1500]}
                forcedReplay={true}>
                <div key='a'>{n.h2[0]}</div>
                <div key='b'>{n.h2[1]}</div>
                <div key='c'>{n.h2[2]}</div>
                <div key='d'>{n.h2[3]}</div>
            </QueueAnim>
        </ScrollOverPack>
    )
}
class LoadIntro extends React.Component {
    componentDidMount() {}
    render() {

        return (<Intro
            key={'intro' + 0}
            data={{
            params: {
                id: 0
            }
        }}/>);
    }
}
export default LoadIntro;
