import React from 'react';
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import Tween from 'rc-tween-one';
import Slide from '@material-ui/core/Slide';
import content from './SectionsContent';
import CloseIcon from '@material-ui/icons/Close';
import DialogTransition from './DialogTransition';
import iniciaScroll from './Scroll';
const Link = ScrollAnim.Link;
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);
function DialTransition(props) {
    return <Slide direction="down" {...props}/>;
}


class CallDialog extends React.Component {
    constructor({data, key, props}) {
        super({data, key, props});
        this.state = {
            open: false,
            dialReverse: true,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };

        this.n = find(data.params.n);
    }
      handleClickOpen = () => {
        this.setState({ dialReverse: false });
        ScrollAnim
    .scrollScreen
    .unMount();
            this.setState({ open: true });


      };

      handleClose = () => {
        this.setState({ dialReverse: true});
        iniciaScroll()
        setTimeout(() => {
            this.setState({ open: false });
         }, 3000);
      };
    render() {
        const n = this.n;
       const dialStyles = {
            opacity: 0,
            transform: 'translateY(0)',
            backgroundColor: '#fff',
            top: '0vh'
        }
        const dialAnimation = {
            from: {
                delay: 1000,
                duration: 600,
                opacity: 0,
                x: 0,
                top: 0,
                ease: "linear",
                backgroundColor: n.cor2
            },
            to: {
                delay: 1000,
                duration: 300,
                opacity: 1,
                x: 0,
                y: '0',
                top: 0,
                ease: "easeOutCirc",
                backgroundColor: '#fff'
            }

        };
        return (

<Dialog 
                fullScreen
                className="dialog"
                transitionComponent={DialTransition}
                id={'anchor'+n.id}
                elevation={8}
                open={this.state.open}
                transitionDuration={{
                enter: 0,
                exit: 0
            }}
            //     style={{
            //     backgroundColor: '#ffffff',
            //     zIndex: 99999,
            //     height: '100%',
            //     position: 'absolute'
            // }}
            >

            <Tween
                component={'section'}
                dialAnimation={[
                {
                    ...dialAnimation.from
                }, {
                    ...dialAnimation.to
                }
            ]}
            style={dialStyles}
            className={"tween-one cabecalho"}>
                <h1 style={{
                    color: '#fff'
                }}>{n.letra}</h1>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>

                <Button
                            style={{
                            backgroundColor: 'transparent',
                            top:0,
                            position: "fixed",
                            zIndex: 19,
                            right: '1em'
                        }}
                            onClick={this.handleClose}
                            >
                                <CloseIcon
                                    style={{
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)'
                                }}/>
                        </Button>
               </Tween>
               {DialogTransition({n: n, key: 'tweenOne3', reverse: this.state.dialReverse, 
                reverseDelay: 0} )}


            </Dialog>
        )
    }
}
CallDialog.propTypes = {
    classes: PropTypes.object.isRequired,
  };
  export default CallDialog;
