import React from 'react';
import PropTypes from 'prop-types';
import Dialog from '@material-ui/core/Dialog';
import ScrollAnim from 'rc-scroll-anim';
import Button from '@material-ui/core/Button';
import Tween from 'rc-tween-one';
import Slide from '@material-ui/core/Slide';
import content from './SectionsContent';
import CloseIcon from '@material-ui/icons/Close';
import DialogTransition from './DialogTransition';
const ScrollOverPack = ScrollAnim.OverPack;

const find = id => content.find(p => p.id === id);
const styles = {
    appBar: {
      position: 'relative',
    },
    flex: {
      flex: 1,
    },
  };
function DialTransition(props) {
    return <Slide direction="down" {...props}/>;
}


class CallDialog extends React.Component {
    constructor({data, props}) {
        super({data, props});
        this.state = {
            open: false,
            buttonEnterReverse: false,
            buttonCloseReverse: false
        };

        this.n = find(data.params.n);
    }
      handleClickOpen = () => {
        setTimeout(() => {
           this.setState({ open: true });
        }, 300);
      };

      handleClose = () => {

        setTimeout(() => {
            this.setState({ open: false });
         }, 3000);
      };
    render() {
        const n = this.n;
        return (
            <div>
            <Button
                className="ver_mais"
                // to={"anchor" + this.n.id}
                onClick={this.handleClickOpen}
                style={{
                position: 'fixed',
                height: '4em',
                bottom: '10vh',
                left: 0,
                zIndex: 999,
                cursor: 'pointer'
            }}>
                 {'ver projeto' }
            </Button>
<Dialog 
                fullScreen
                className="dialog"
                id={'anchor'}
                elevation={8}
                open={this.state.open}
                transitionDuration={{
                enter: 0,
                exit: 0
            }}
                TransitionComponent={DialTransition}
            //     style={{
            //     backgroundColor: '#ffffff',
            //     zIndex: 99999,
            //     height: '100%',
            //     position: 'absolute'
            // }}
            >
            <ScrollOverPack
            always={true}
            appear={true}
                componentProps={{ tweenOne2:  {reverse :true }, tweenOne:  {reverse :true } }}
            className={n.classe}
            id={n.hash}
            playScale={0.5}>
                    <div className="tween-one cabecalho">
                <h1 style={{
                    color: '#fff'
                }}>{n.letra}</h1>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                <label>{n.label}</label>
                <h2>{n.h2}</h2>
                <div className='descricao'>{n.descricao}</div>
                </div>
                <Button
                            style={{
                            backgroundColor: 'transparent',
                            top:0,
                            position: "fixed",
                            zIndex: 19,
                            right: '1em'
                        }}
                            onClick={this.handleClose}
                            >
                                <CloseIcon
                                    style={{
                                    width: 'calc((100vw+100vh)/2/12)',
                                    height: 'calc((100vw+100vh)/2/12)'
                                }}/>
                        </Button>

            {DialogTransition({n: n})}
        </ScrollOverPack>   

            </Dialog>
            </div>
        )
    }
}
CallDialog.propTypes = {
    classes: PropTypes.object.isRequired,
  };
  export default CallDialog;
