import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import './assets/styles/main.css';


const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 1000 });

class Demo extends React.Component {
  constructor() {
    super();
    this.state = {
      show: false,
    };
  }

  componentDidMount() {
    EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
    setTimeout(() => {
      this.setState({ show: true });
      console.log(this)
    }, 1500);


    window.addEventListener('resize',  this.handleWindowSizeChange.bind(this));
    window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));

  }
  onFocus = (e) => {
    this.dom = e.target;
    this.setState({ show: true });
    // to.className = "active";
    this.barAnimate();

    const ida = document.getElementById(e.to)

    console.log(ida.className);

  }

  onBlur = (e) => {
    this.setState({ show: false });
  }
  barAnimate() {
    if (!this.dom) {
      return;
    }
    const bar = this.bar;
    bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
  }


  handleWindowSizeChange = () => {
    ScrollAnim.scrollScreen.init({ loop: false, duration: 1200, ease: "easeInOutQuart", scrollInterval: 1000 });
  };

  render() {
    var className = this.state.show ? 'click-state' : 'base-state';
    return (
      <div className="container">
      <div className="nav">

        <div className="logo">
          <p>Ale Almeida</p>
        </div>
        <div className="nav-wap">
        <Link className="nav-list" to="page0" showHeightActive={['50%', '10%']}
            onFocus={this.onFocus}
            ref={(c) => {
              this.dom = ReactDOM.findDOMNode(c);
            }}
          >Example1</Link>
          <Link className="nav-list" to="page1"  showHeightActive={['0%', '50%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example2
          </Link>
          <Link className="nav-list" to="page2" showHeightActive={['0%', '50%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example3
          </Link>
          <Link className="nav-list" to="page3" showHeightActive={['0%', '50%']}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example4
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
      </div>
      <Element className={"pack-page page0"+ className} onFocus={this.onFocus} id="page0">
      <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[50, 1000]} delay={1000}
          duration={[1000, 3000]}
          ease={'easeInOutQuart'} leaveReverse>
        <div key='a'>Olá!</div>
        <div key='b'>Sou Alexandre Almeida,</div>
        <div key='c'>Senior Product Designer na Ogilvy & Mather Brasil,</div>
        <div key='d'></div>
        <div key='e'>São Paulo.</div>
      </QueueAnim>
      </Element>
        <ScrollOverPack
          id="page1"
          className={"page1 fade-leave" + className} onFocus={this.onFocus} 
          onBlur={this.onBlur}
          ease="linear"
          location="page1"
          playScale={0.7}
          repeat={false}
        >
          <TweenOne 
          onFocus={this.onFocus} className="tween-one" key="0"
          animation={{  delay: 1, duration: 0, translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Pop</h2>
            <div>Mini Pop Credicard</div>
          </TweenOne>
        </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page2"
        style={{ backgroundColor: '#ccc' }}
        id="page2"
        ease="linear"
        playScale={0.8}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Caixa</h2>
            <div>Nova Caixa</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page3"
        id="page3"
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.8}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Vivo</h2>
            <div>Novo Vivo Easy</div>
          </TweenOne>
      </ScrollOverPack>
    </div>

    );

  }

}

ReactDOM.render(<Demo />, document.getElementById('root'));

