    render() {
        return (
            <span>
                <Intro/>
                <Conteudo
                    data={{
                    params: {
                        id: 1
                    }
                }}/>
                <Conteudo
                    data={{
                    params: {
                        id: 2
                    }
                }}/><Conteudo
                    data={{
                params: {
                    id: 3
                }
            }}/><Conteudo
                    data={{
                params: {
                    id: 4
                }
            }}/>
                <div
                    style={{
                    width: '100%',
                    height: '100vh',
                    position: 'fixed',
                    backgroundColor: '#f0f0f0',
                    zIndex: 6,
                    top: 0
                }}></div>
            </span>
        );
    }
}

export default Sections;
