import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import { HashRouter as Router, Route} from 'react-router-dom';

import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import './assets/styles/main.css';
import routes from './components/ui/MenuContent';
// import Intro from './components/ui/Intro';
import content from './components/ui/SectionsContent';


const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: false, duration: 1500, ease: "easeInOutQuart", scrollInterval: 800 });




class Demo extends React.Component {
  constructor() {
    super();
    this.state = {
      show: false,
    };
  }

  componentDidMount() {
    EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
    setTimeout(() => {
      this.setState({ show: true });
    }, 1500);
    window.addEventListener('resize',  this.handleWindowSizeChange.bind(this));
    window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));

  }
  onFocus = (e) => {
    this.dom = e.target;

    console.log(this.dom)

    this.barAnimate();
    var idOn = document.getElementById(e.to)
    // if(e.target.index)

        idOn.className =  "pack-page " + e.to + " active"
  }
  onBlur = (e) => {
    var idOn = document.getElementById(e.to);
    idOn.className =  "pack-page " + e.to;
  }
  barAnimate = () => {
    if (!this.dom) {
      return;
    }
    const bar = this.bar;
    bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
  }

  handleWindowSizeChange = () => {
    ScrollAnim.scrollScreen.init({ loop: false, duration: 1500, ease: "easeInOutQuart", scrollInterval: 800 });
  };

  render() {
    return (

      <div className="container">

          <div className="nav">
            <div className="nav-wap">
            <Link className="nav-list" to="page0" showHeightActive={['50%', '10%']}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example
          </Link>
          <Link className="nav-list" to="page1" showHeightActive={['10%', '60%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example2
          </Link>
          <Link className="nav-list" to="page2" showHeightActive={['60%', '50%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example3
          </Link>
          <Link className="nav-list" to="page3" offsetTop={100}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example4
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
          </div>
        {/* <Intro /> */}

          <Element id={'page0'} index={'0'} className={"pack-page page0"}>
            <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[200, 700]} delay={800}
                duration={[800, 500]}
                always={false}>
                <div key='a'>Olá!</div>
                <div key='b'>Sou Alexandre Almeida,</div>
                <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br">Ogilvy & Mather Brasil</a>,</div>
                <div key='d'></div>
                <div key='e'>São Paulo.</div>
            </QueueAnim>
        </Element>
          {content.map((route) => (
            <ScrollOverPack
              id={route.to} className={"pack-page  "+ route.to}
                style={{ backgroundColor: '#f0f0f0' }}
                playScale={0.1}
                >
                <TweenOne 
                      className="tween-one" key="0"
                      animation={{  delay: 1, duration: 0, translateY: 0, opacity: 1 }}
                      style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
                      >
                          <h2>{route.titulo}</h2>
                          <div>Mini Pop Credicard</div>
                      </TweenOne>
            </ScrollOverPack>
          ))}
      </div>

    );

  }

}

ReactDOM.render([<Demo />], document.getElementById('root'));

