import React from 'react';
import TweenOne from 'rc-tween-one';


function DialogTransition({a, key, reverse}) {

    const styles = {
        width: '100%',
        height: '90vh',
        position: 'absolute',
        backgroundColor: a.cor2,
        opacity: 1,
        top: 0,
        left: 0,
        borderRadius: '60%',
        transform: 'translateX(calc(-100vw + (100vw+100vh)/2*1.27 )) translateY(-45vh) scale(0.00001, 0.4)'
    }
    const dialAnimation = {
        from: 
            {
                delay: 0,
                        duration: 800,
                        opacity: 0,
                        top: '0',
                        left:'-35.5vw',
                        ease: "easeInQuint",
                        backgroundColor: a.cor2,
                        type: 'from',
                        scaleX: 0, scaleY:0,
                        x: '0',
                        y: '-200'
            }
        ,
        to: 
            {
                delay: 0,
                        duration: 1200,
                        opacity: 1,
                        top: '0',
                        ease: "easeInOutExpo",
                        backgroundColor: a.cor2,
                        type: 'to',
                        borderRadius: '14%',
                        scaleX: 2, scaleY: 1,
                        x: 0,
                        y: 0
            }

    };
    return (
        <TweenOne 
        reverseDelay={800}
        key={key}
        reverse={reverse}
                animation={
                [dialAnimation.from, dialAnimation.to]
            }
                style={styles}/>
    )
}

export default DialogTransition;
