const posts = [
  {
    path: "/sandwiches",
    component: "Sandwiches"
  },
  {
    path: "/tacos",
    component: "Tacos",
    posts: [
      {
        path: "/tacos/bus",
        component: "Bus"
      },
      {
        path: "/tacos/cart",
        component: "Cart"
      }
    ]
  }
];

function RouteWithSubRoutes(route) {
  return (
    <Route
      path={route.path}
      render={props => (
        // pass the sub-routes down to keep nesting
        <route.component {...props} posts={route.posts} />
      )}
    />
  );
}
function Menu() {
    <Router>
      <div>
      {posts.map((route, i) => (
          <RouteWithSubRoutes key={i} {...route} />
        ))}
      </div>
    </Router>
}

