import React from 'react';
import Menu from './ui/Menu';
import Intro from './ui/Intro';
import Sections from './ui/Sections';
import Dials from './ui/Dials';
import ScrollAnim from 'rc-scroll-anim';
const EventListener = ScrollAnim.Event;


class App extends React.Component {
    componentDidMount() {
        EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
        window.addEventListener('load', this.refreshPage.bind(this));
        window.addEventListener('resize', this.handleWindowSizeChange.bind(this));
        window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));
    }
    handleWindowSizeChange = () => {
        setTimeout(() => {
            // iniciaScroll()
            }, 50);
    };
    // refreshPage = () => {
    //     setTimeout(() => {
    //         // window.scrollTo(0, 0);
    //     }, 5);

    // }
    onFocus = (e) => {
        this.dom = e.target;
        this.barAnimate();
        var idOn = document.getElementById(e.to)
        idOn.className = "pack-page " + e.to + " active"


        var idData = idOn.getAttribute('data')
        var idDial = document.getElementById(idData)
        // idDial.className = 'dial active'
        idDial.style.display = 'block'

    }
    onBlur = (e) => {
        var idOn = document.getElementById(e.to);
        var idData = idOn.getAttribute('data')
        idOn.className = "pack-page " + e.to;
        // if(e.target.index)
        var idDial = document.getElementById(idData)
        // idDial.className = "dial"
        idDial.style.display = 'none'
    }
    barAnimate = () => {
        if (!this.dom) {
            return;
        }
        const bar = this.bar;
        bar.style.left = `$ {this
            .dom
            .getBoundingClientRect()
.left}px`;
    }
    render() {
        return (
            <div>
            <div className="container">
                <Menu/>
                <Intro/>
                <Sections/>
            </div>
                <Dials/>
                </div>
        );
    }
}

export default App;
