import ScrollAnim from 'rc-scroll-anim';
import React from 'react';
import ReactDOM from 'react-dom';
import QueueAnim from 'rc-queue-anim';
import TweenOne from 'rc-tween-one';
import './assets/styles/main.css';
import Menu from './components/ui/Menu';

const Link = ScrollAnim.Link;
const Element = ScrollAnim.Element;
const ScrollOverPack = ScrollAnim.OverPack;
const EventListener = ScrollAnim.Event;
ScrollAnim.scrollScreen.init({ loop: false, duration: 1000, ease: "easeInOutQuart", scrollInterval: 800 });

class Demo extends React.Component {
  constructor() {
    super();
    this.state = {
      show: false,
    };
  }

  componentDidMount() {
    EventListener.addEventListener('resize.userResize', this.barAnimate.bind(this));
    setTimeout(() => {
      this.setState({ show: true });
    }, 1500);


    window.addEventListener('resize',  this.handleWindowSizeChange.bind(this));
    window.removeEventListener('resize', this.handleWindowSizeChange.bind(this));

  }
  onFocus = (e) => {
    this.dom = e.target;
    // to.className = "active";

    this.barAnimate();
    var idOn = document.getElementById(e.to)
    // if(e.target.index)

        // idOn.className =  "pack-page " + e.to + " active"

  //  if(e.target.className(active) { this.setState({ selected: true }); console.log(idOn.className);}
    // this.state.selected


    // this.state.selected ? 'click-state' : 'base-state';
  }


  onBlur = (e) => {
    // var idOn = document.getElementById(e.to)
    // idOn.className =  "pack-page " + e.to 
  }
  barAnimate() {
    if (!this.dom) {
      return;
    }
    const bar = this.bar;
    bar.style.left = `${this.dom.getBoundingClientRect().left}px`;
  }


  handleWindowSizeChange = () => {
    ScrollAnim.scrollScreen.init({ loop: false, duration: 1000, ease: "easeInOutQuart", scrollInterval: 800 });
  };

  render() {
    var className = this.state.selected ? 'click-state' : 'base-state';
    return (
      <div className="container">
      <div className="nav">

        <div className="logo">
          <p>Ale Almeida</p>
        </div>
        <div className="nav-wap">
          <Menu />
          <Link className="nav-list" to="page1" index={1} showHeightActive={['0%', '50%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example2
          </Link>
          <Link className="nav-list" to="page2" index={2} showHeightActive={['0%', '50%']} toShowHeight
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example3
          </Link>
          <Link className="nav-list" to="page3" index={3} showHeightActive={['0%', '50%']}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example4
          </Link>
          <Link className="nav-list" to="page4" index={4} showHeightActive={['0%', '50%']}
            onFocus={this.onFocus}
            onBlur={this.onBlur}
          >
            Example5
          </Link>
          <div ref={(c) => { this.bar = c; }} className="nav-bar" />
        </div>
      </div>
      <Element id="page0" index={0} className="pack-page page0" >
      <QueueAnim key='queueAnim' type={['right','alpha']} className="intro" interval={[100, 500]} delay={800}
          duration={[1500, 2000]}
          ease={'easeInOutQuart'} leaveReverse>
        <div key='a'>Olá!</div>
        <div key='b'>Sou Alexandre Almeida,</div>
        <div key='c'>Senior Product Designer na <a href="http://ogilvy.com.br" target="_blank">Ogilvy & Mather Brasil</a>,</div>
        <div key='d'></div>
        <div key='e'>São Paulo.</div>
      </QueueAnim>
      </Element>
        <ScrollOverPack
          id="page1" index={1}
          className="pack-page page1"
          ease="linear"
          location="page1"
          style={{ backgroundColor: '#f0f0f0' }}
          playScale={0.1}
          repeat={false}
        >
          <TweenOne 
          className="tween-one" key="0"
          animation={{  delay: 1, duration: 0, translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Pop</h2>
            <div>Mini Pop Credicard</div>
          </TweenOne>
        </ScrollOverPack>
      <ScrollOverPack
        id="page2"
        index={2}
        className="pack-page page2"
        style={{ backgroundColor: '#ccc' }}
        ease="linear"
        playScale={0.1}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Caixa</h2>
            <div>Nova Caixa</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page3"
        id="page3"
        index={3}
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.1}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Vivo</h2>
            <div>Novo Vivo Easy</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page4"
        id="page4"
        index={4}
        style={{ backgroundColor: '#ffffff' }}
        ease="linear"
        playScale={0.1}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>RB</h2>
            <div>Novo Reckitt</div>
          </TweenOne>
      </ScrollOverPack>
      <ScrollOverPack
        className="pack-page page5"
        id="page5"
        index={5}
        style={{ backgroundColor: '#f0f0f0' }}
        ease="linear"
        playScale={0.1}
        repeat={0}
      >
        <TweenOne className="tween-one" key="0" 
          animation={{  delay: 1, duration: 0,translateY: 0, opacity: 1 }}
          style={{ transform: 'translateY(0px)', opacity: 0, position: 'fixed', top: 50, height: 1000, 'z-index': 7  }}
          >
            <h2>Nacional Gás</h2>
            <div>Nacional Gás</div>
          </TweenOne>
      </ScrollOverPack>
    </div>

    );

  }

}

ReactDOM.render(<Demo />, document.getElementById('root'));

